import React from 'react';
import { View, Image, StyleSheet } from 'react-native';
import { Divider } from 'react-native-paper';
import Text from './Text';

const styles = StyleSheet.create({
  container: {
    zIndex: 2,
    backgroundColor: 'red'
  },
  top: {
    flexDirection: 'row'
  },
  avatarContainer: {
    flexGrow: 0,
    marginRight: 20,
  },
  nameText: {
    marginBottom: 5,
  },
  descriptionText: {
    flexGrow: 1,
  },
});


const ReviewItem = ({ review }) => {
  const {
    nosing,
    taste,
    finish,
    rating,
    comment,
  } = review;

  return (
    <View style={styles.container}>
          <View style={styles.avatarContainer}>
              {/* <Image source={{ uri: downloadURL }} style={styles.avatar} />  user avatar*/}
          </View>
          <View>
          <Text
            style={styles.nameText} 
            fontSize="subheading"
            testID="whiskyItemRating"
          >
            Rating: {rating}
          </Text>
          <Text
            style={styles.nameText} 
            fontSize="subheading"
            testID="whiskyItemRating"
          >
            User: 
          </Text>
          <Text
            style={styles.nameText} 
            fontSize="subheading"
            testID="whiskyItemRating"
          >
            Time: 
          </Text>

          </View>


          <Text
            style={styles.nameText}
            fontSize="subheading"
            testID="whiskyItemNosing"
          >
            Nosing: {nosing}
          </Text>
          <Text
            style={styles.nameText}
            fontSize="subheading"
            testID="whiskyItemTaste"
          >
            Taste: {taste}
          </Text>
          <Text
            style={styles.nameText}
            fontSize="subheading"
            testID="whiskyItemFinish"
          >
            Finish: {finish}
          </Text>
          <Text
            style={styles.nameText}
            fontSize="subheading"
            testID="whiskyItemComment"
          >
            Comment: {comment}
          </Text>

    </View>
  );
};

export default ReviewItem;
