import React, {useState} from 'react';
import { StyleSheet, View } from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useHistory } from 'react-router-native';
import { useMutation } from '@apollo/react-hooks';
import { signIn } from '../firebase/firebaseFunctions';

import Button from './Button';
import FormikTextInput from './FormikTextInput';
import useSignIn from '../hooks/useSignIn';
import { CREATE_USER } from '../graphql/mutations';

import Text from './Text';

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    padding: 15,
  },
  fieldContainer: {
    marginBottom: 15,
  },
});

const initialValues = {
  name: '',
  password: '',
  passwordConfirmation: '',
};

const validationSchema = yup.object().shape({
  name: yup
    .string()
    .min(1, 'Name must be at least 1 character long')
    .max(30, 'Name must be at most 30 characters long')
    .required('Name is required'),
  email: yup
    .string()
    .required('Email is required'),
  password: yup
    .string()
    .min(5, 'Password must be at least 5 characters long')
    .max(50, 'Password must be at least 50 characters long')
    .required('Password is required'),
  passwordConfirmation: yup
    .string()
    .oneOf(
      [yup.ref('password'), null],
      'Password confirmation must match the password',
    )
    .required('Password confirmation is required'),
});

const SignUpForm = ({ onSubmit }) => {
  return (
    <View style={styles.container}>

      <View style={styles.fieldContainer}>
        <FormikTextInput name="email" placeholder="Email" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput name="name" placeholder="Name" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="password"
          placeholder="Password"
          secureTextEntry
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="passwordConfirmation"
          placeholder="Password confirmation"
          secureTextEntry
        />
      </View>

      <Button onPress={onSubmit} testID="submitButton">
        Sign up
      </Button>
    </View>
  );
};

const SignUp = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [signIn] = useSignIn();
  const history = useHistory();

  const onSubmit = async (values) => {
    const { email, name, password } = values;

    const user = {
      email,
      name,
      password,
    };

    await createUser({ variables: { user } });
    await signIn(user);

    history.push('/');
  };

  return (

    <Formik
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
    >
      {({ handleSubmit }) => <SignUpForm onSubmit={handleSubmit} />}
    </Formik>
  );
};

export default SignUp;
