import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import { Button } from 'react-native-paper';
import { Formik } from 'formik';
import { useHistory } from 'react-router-native';
import * as yup from 'yup';
import FormikTextInput from './FormikTextInput';
import { DatePickerModal } from 'react-native-paper-dates';
import CustomButton from './CustomButton';

const styles = StyleSheet.create({
    container: {
      flexDirection: 'column',
      justifyContent: 'center',
      flexWrap: 'wrap',
      alignContent: 'center',
      height: '100%',
      width: '100%',
      position: 'absolute',
      zIndex: 1
    },
    fieldContainer: {
      marginBottom: 15,
    },
  });

const initialValues = {
    firstName: '',
    lastName: '',
    birthdate: '',
    country: '',
    bio: ''

};

const validationSchema = yup.object().shape({
    firstName: yup
        .string()
        .min(1, 'First name must be at least 1 charaters long')
        .required('First name is required'),
    lastName: yup
        .string()
        .min(1, 'Last name must be at least 1 characters long')
        .required('Last name is required'),
    birthdate: yup
        .date()
        .min(8, 'Birthdate must be atleast 8 characters')
        .max(8, 'Birthdate must be at most 8 characters long')
        .required('Birtdate is required'),
    country: yup
        .string()
        .required('Country is required'),
    bio: yup
        .string()
        .max(150, 'Bio can be at most 150 characters long'),
});

const PickDate = () => {

    const [date, setDate] = useState();
    const [open, setOpen] = useState(false);

    const onDismissSingle = React.useCallback(() => {
        setOpen(false);
    }, [setOpen]);

    const onConfirmSingle = React.useCallback(
        (params) => {
            setOpen(false);
            setDate(params.date);
        },
        [setOpen, setDate]
    );

    return (
        <>
            <Button onPress={() => setOpen(true)} uppercase={false} mode="outlined">
                Pick single date
      </Button>
            <DatePickerModal
                // locale={'en'} optional, default: automatic
                mode="single"
                visible={open}
                onDismiss={onDismissSingle}
                date={date}
                onConfirm={onConfirmSingle}
            // onChange={} // same props as onConfirm but triggered without confirmed by user
            // saveLabel="Save" // optional
            // label="Select date" // optional
            // animationType="slide" // optional, default is 'slide' on ios/android and 'none' on web
            />
        </>
    );
};

const UserinfoForm = ({ onSubmit }) => {
    return (
        <View style={styles.container}>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="firstName"
                    placeholder="First name" />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="lastName"
                    placeholder="Last name" />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="birthdate"
                    placeholder="Birthdate"
                />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="country"
                    placeholder="Country"
                />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="bio"
                    placeholder="Bio"
                    multiline
                    numberOfLines={4}
                />
            </View>

        <Button mode={'outlined'} onPress={onSubmit}>
            Submit
        </Button>

        </View>
    );
};

const UploadUserInfo = () => {

    const history = useHistory();

    // const onSubmit = async (values) => {
    //     const { email, password } = values;

    //     SignUpWithEmail(email, password);

    //     history.push("/choose-pic");
    // };


    return (
            <>
            <Formik
                style={styles.container}
                initialValues={initialValues}
                onSubmit={()=>{history.push('/');}}
                validationSchema={validationSchema}
            >
                {({ handleSubmit }) => <UserinfoForm onSubmit={handleSubmit} />}
            </Formik>
            </>
    );
};

export default UploadUserInfo;
