import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, ScrollView} from 'react-native';
import { Button, Avatar, Divider, IconButton  } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import * as firebase from 'firebase';
import { array } from 'yup';


const styles = StyleSheet.create({

  container: {
    position: 'absolute',
    top: 100

  }

});


const MyInfo = () => {

  const history = useHistory();
  const [files, setFiles] = useState([]);

  // put data into array

  useEffect(() => {
    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .get()
      .then((querySnapshot) => {
        let urls = [];
        querySnapshot.forEach((doc) => {
          urls.push(doc.data().downloadURL);
        });
        console.log('urls: ', urls);
      })
      .catch((error) => {
        console.log("Error getting documents: ", error);
      });

  });


  return (
      <View style={styles.container}>
      <IconButton 
        icon={'check-circle-outline'} size={100} style={styles.iconButton} color='#2cf52c' />
      </View>
  );
};

export default MyInfo;
