import React from 'react';
import { View, Image, StyleSheet } from 'react-native';
import Text from './Text';

const styles = StyleSheet.create({
  container: {
    zIndex: 5,
    height: '100%',
    width: '100%',
    position: 'absolute',
    paddingVertical: 80,
    backgroundColor: 'red'
  },
  avatarContainer: {
    flexGrow: 0,
    marginRight: 20,
  },
  nameText: {
    marginBottom: 5,
  },
  descriptionText: {
    flexGrow: 1,
  },
});


const ReviewItem = ({ review }) => {
  const {
    nosing,
    taste,
    finish,
    rating,
    comment,
    downloadURL,
  } = review;

  return (
    <View style={styles.container}>
          <View style={styles.avatarContainer}>
              <Image source={{ uri: downloadURL }} style={styles.avatar} />
          </View>
          <Text
            style={styles.nameText}
            fontWeight="bold"
            fontSize="subheading"
            testID="whiskyItemNosing"
          >
            Nosing: {nosing}
          </Text>
          <Text
            style={styles.nameText}
            fontWeight="bold"
            fontSize="subheading"
            testID="whiskyItemTaste"
          >
            Taste: {taste}
          </Text>
          <Text
            style={styles.nameText}
            fontWeight="bold"
            fontSize="subheading"
            testID="whiskyItemFinish"
          >
            Finish: {finish}
          </Text>
          <Text
            style={styles.nameText}
            fontWeight="bold"
            fontSize="subheading"
            testID="whiskyItemComment"
          >
            Comment: {comment}
          </Text>
          <Text
            style={styles.nameText} 
            fontWeight="bold"
            fontSize="subheading"
            testID="whiskyItemRating"
          >
            Rating: {rating}
          </Text>
    </View>
  );
};

export default ReviewItem;
