import React, { useState, useEffect } from 'react';
import { StyleSheet, View, ScrollView, Image, ImageEditor } from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useHistory } from 'react-router-native';
import uuid from 'uuid';
import * as ImagePicker from 'expo-image-picker';
import { Button, Checkbox } from 'react-native-paper';
import Text from './Text';
import FormikTextInput from './FormikTextInput';

import * as firebase from 'firebase';


const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  fieldContainer: {
    margin: 5,
  },
  heading: {
    alignItems: 'center',
    marginBottom: 30
  },
  buttoncontainer: {

  }
});

const initialValues = {
  distillery: '', // 
  brand: '', // 
  nameAddition: '',
  country: '', //
  region: '',
  category: '', //
  age: '', 
  abv: '', // 
  bottleSize: '', //
  bottler: '',
  series: '',
  description: '',
  caskType: '',
  caskNumber: '',
  numberOfBottles: '',
};
//distillationDate
//bottlingDate
//chill filtration checkbox
//artificial coloring checkbox
//brand-ddate/bdate-nameadd-abv-size

const validationSchema = yup.object().shape({
  distillery: yup
    .string()
    .required('Distillery is required'),
  brand: yup
    .string()
    .required('Brand is required'),
  nameAddition: yup
    .string(),
  country: yup
    .string()
    .required('Country is required'),
  region: yup
    .string(),
  category: yup
    .string()
    .required('Category is required'),
  age: yup 
    .number()
    .required('Age is required'),
  abv: yup
    .number()
    .required('Abv is required'),
  bottleSize: yup
    .number()
    .required('Bottlesize is required'),
  bottler: yup
    .string(),
  series: yup
    .string(),
  description: yup
    .string()
    .required('Description is required'),
  caskType: yup
    .string(),
  caskNumber: yup
    .number(),
  numberOfBottles: yup
    .number()
});



const UploadWhiskyForm = ({ onSubmit, pickProfilePicFromLibrary }) => {
  return (
    <View>
      <View style={styles.fieldContainer}>
        <FormikTextInput 
        name="distillery" 
        placeholder="Distillery" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="brand"
          placeholder="Brand"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="nameAddition"
          placeholder="Name addition"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="country"
          placeholder="Country"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="region"
          placeholder="Region"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="category"
          placeholder="Category"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="age"
          keyboardType="numeric"
          placeholder="Age"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="abv"
          keyboardType="numeric"
          placeholder="Abv"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="bottleSize"
          keyboardType="numeric"
          placeholder="Bottlesize"
        />
      </View>


      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="bottler"
          placeholder="Bottler"
        />
      </View>


      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="series"
          placeholder="Series"
        />
      </View>

      <Checkbox status={'checked'}
        onPress={() => {
          console.log('pressed');
        }} />

      <Checkbox status={'checked'}
        onPress={() => {
          console.log('pressed');
        }} />

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="description"
          placeholder="Description"
        />
      </View>


      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="caskType"
          placeholder="Cask Type"
        />
      </View>


      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="caskNumber"
          keyboardType="numeric"
          placeholder="Cask Number"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="numberOfBottles"
          keyboardType="numeric"
          placeholder="Number of bottles"
        />
      </View>

      <Button icon='camera' mode='outlined' onPress={pickProfilePicFromLibrary} style={styles.buttoncontainer}>Upload image</Button>

      <Button icon={'bottle-wine'} style={styles.fieldContainer} mode='outlined' onPress={onSubmit}>
          Submit whisky
      </Button>
    </View>
  );
};



const UploadWhisky = () => {
  const history = useHistory();
  const [image, setImage] = useState();
  const [visible, setVisible] = useState(false);
  const onToggleSnackBar = () => setVisible(!visible);
  const [nWhiskyID, setNWhiskyID] = useState('');
  const [downloadURL, setDownloadURL] = useState('');


  const addImageToFirestore = async (downloadURL, whiskyID) => { 
    setDownloadURL(downloadURL);

    firebase.firestore()
    .collection('whiskyImages')
    .doc(whiskyID)
    .set({
      downloadURL
    });
  };

  const pickProfilePicFromLibrary = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    const whiskyID = uuid.v4();
    setNWhiskyID(whiskyID);

    if (!result.cancelled) {
      uploadImage(result.uri, whiskyID);
      setImage(result.uri);
      onToggleSnackBar();
    }
  };

  const uploadImage = async(uri, whiskyID) => {

    const response = await fetch(uri);
    const blob = await response.blob();

    const ref = firebase.storage().ref().child(`images/whiskies/${whiskyID}`);

    const uploadTask = ref.put(blob);

    uploadTask.on('state_changed', 
  (snapshot) => {
    // Observe state change events such as progress, pause, and resume
    // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    console.log('Upload is ' + progress + '% done');
    switch (snapshot.state) {
      case firebase.storage.TaskState.PAUSED: // or 'paused'
        console.log('Upload is paused');
        break;
      case firebase.storage.TaskState.RUNNING: // or 'running'
        console.log('Upload is running');
        break;
    }
  }, 
  (error) => {
    // Handle unsuccessful uploads
    switch (error.code) {
      case 'storage/unauthorized':
        // User doesn't have permission to access the object
        console.log(error.code);
        break;
      case 'storage/canceled':
        // User canceled the upload
        console.log(error.code);
        break;
      case 'storage/unknown':
        // Unknown error occurred, inspect error.serverResponse
        console.log(error.code);
        break;
    }
  },
  () => {
    // Handle successful uploads on complete
    // For instance, get the download URL: https://firebasestorage.googleapis.com/...
    uploadTask.snapshot.ref.getDownloadURL().then((downloadURL) => {
      console.log('File available at', downloadURL);
      addImageToFirestore(downloadURL, whiskyID);
    });
  });

  };


  const onSubmit = async (values) => {
    const {name, description, distillery, country, type, age ,abv, bottlesize} = values;

    firebase.firestore().collection('whiskies').doc(nWhiskyID).set({
      name: name,
      description: description,
      distillery: distillery,
      country: country,
      type: type,
      age: age,
      abv: abv,
      bottlesize: bottlesize,
      downloadURL: downloadURL
    });
    history.push('/');
  };

  return (
    <View style={styles.container}>
      <ScrollView>
      <View style={styles.heading}>
        <Text color={'primary'} fontSize={'heading'} fontWeight={'bold'}>
          Upload Whisky
        </Text>
      </View>

      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        {({ handleSubmit }) => <UploadWhiskyForm onSubmit={handleSubmit} pickProfilePicFromLibrary={pickProfilePicFromLibrary} />}
      </Formik>
      {image && <Image source={{ uri: image }} style={{ width: 100, height: 100 }} />}
      </ScrollView>
    </View>
  );
};

export default UploadWhisky;
