import React, { useState, useEffect } from 'react';
import { FlatList, View, StyleSheet, TouchableOpacity } from 'react-native';
import { useHistory } from 'react-router-native';
import { Searchbar } from 'react-native-paper';
import { useDebounce } from 'use-debounce';

import WhiskyItem from './WhiskyItem';
import { useCollection } from 'react-firebase-hooks/firestore';
// import useRepositories from '../hooks/useRepositories';
import Picker from './Picker';
import * as firebase from 'firebase';

const styles = StyleSheet.create({
    separator: {
        height: 10,
    },
    headerContainer: {
        padding: 15,
    },
    searchContainer: {
        marginBottom: 15,
    },
});

const ItemSeparator = () => <View style={styles.separator} />;

const orderByOptions = [
    { label: 'Latest repositories', value: 'latest' },
    {
        label: 'Highest rated repositories',
        value: 'highestRating',
    },
    {
        label: 'Lowest rated repositories',
        value: 'lowestRating',
    },
];

const WhiskyListHeader = (
    {
      onOrderByChange,
      orderBy,
      searchKeyword,
      onSearchKeywordChange,
    }
) => {
    return (
        <View style={styles.headerContainer}>
            <View style={styles.searchContainer}>
                <Searchbar
                  placeholder="Search whiskies"
                  value={searchKeyword}
                  onChangeText={onSearchKeywordChange}
                />
            </View>
            <Picker
            placeholder={{}}
            onValueChange={onOrderByChange}
            value={orderBy}
            items={orderByOptions}
            />
        </View>
    );
};

export class WhiskyListContainer extends React.Component {
    renderHeader = () => {
        const {
            onOrderByChange,
            orderBy,
            searchKeyword,
            onSearchKeywordChange,
        } = this.props;

        return (
            <WhiskyListHeader
                onOrderByChange={onOrderByChange}
                orderBy={orderBy}
                searchKeyword={searchKeyword}
                onSearchKeywordChange={onSearchKeywordChange}
            />
        );
    };

