}

const WhiskyList = () => {
    const history = useHistory();
    const [orderBy, setOrderBy] = useState('latest');
    const [searchKeyword, setSearchKeyword] = useState('');
    const [whiskies, setWhiskies] = useState('');

    useEffect(() => {
        firebase.firestore()
            .collection('whiskies')
            .get()
            .then((querySnapshot) => {
                let posts = querySnapshot.docs.map(doc => {
                    const data = doc.data();
                    const id = doc.id;
                    return { id, ...data };
                });
                setWhiskies(posts);
            })
