import React from 'react';
import { View, Image, StyleSheet } from 'react-native';
import { Button } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import Text from './Text';
import * as firebase from 'firebase';

const styles = StyleSheet.create({
  backgroundCountainer: {
    backgroundColor: 'orange'
  },
  container: {
    padding: 5,
    width: '100%',
    flexDirection: 'row',
    backgroundColor: 'yellow'
  },
  contentContainer: {
    width: '100%',
    backgroundColor: 'green'
  },

  avatarContainer: {
    marginRight: 5,
  },
  nameText: {
    marginBottom: 5,
  },
  avatar: {
    width: 80,
    height: 160,
  },
});



const UserItem = ({ user }) => {





  const history = useHistory();

  // collections, wishlist, reviews

  return (
    <View style={styles.backgroundCountainer}>


    <View style={styles.container}>

        <View style={styles.avatarContainer}>
          <Image source={{ uri: user.downloadURL}} style={styles.avatar} />
        </View>
        <View style={styles.contentContainer}>
          <Text
            style={styles.nameText}
            fontSize='subheading'
            fontWeight='bold'
          >
              {user.email}
          </Text>

          <View>
              <Button mode="outlined" icon="message" onPress={()=> history.push(`/messenger/${user.id}`)}>Send message</Button>
          </View>

      </View>

    </View>
    </View>
  );
};

export default UserItem;
