import React from 'react';
import { StyleSheet, View } from 'react-native';
import {Button} from 'react-native-paper';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useHistory } from 'react-router-native';
import * as firebase from 'firebase';
import FormikTextInput from './FormikTextInput';
import { useParams } from 'react-router-native';

const styles = StyleSheet.create({
  container: {
    justifyContent: 'center',
    width: '100%',
    height: '100%',
    position: 'absolute',
    zIndex: 1,
  },
  fieldContainer: {
    marginBottom: 15,
  },
});

const initialValues = {
  nosing: '',
  taste: '',
  finish: '',
  rating: '',
  comment: ''
};

const validationSchema = yup.object().shape({
  nosing: yup.string().required('Nosing is required'),
  taste: yup.string().required('Taste is required'),
  finish: yup.string().required('Finish is required'), 
  rating: yup
  .number('Rating must be a number')
  .min(0, 'Rating must be greater or equal to 0')
  .max(100, 'Rating must be less or equal to 100')
  .required('Rating is required'),
  comment: yup.string(),
});

const CreateReviewForm = ({ onSubmit }) => {
  return (
    <View >
      <View style={styles.fieldContainer}>
        <FormikTextInput placeholder="Nosing" name="nosing" />
      </View>
      <View style={styles.fieldContainer}>
        <FormikTextInput placeholder="Taste" name="taste" />
      </View>
      <View style={styles.fieldContainer}>
        <FormikTextInput placeholder="Finish" name="finish" />
      </View>
      <View style={styles.fieldContainer}>
        <FormikTextInput
          placeholder="Rating between 0 and 100"
          keyboardType="numeric"
          name="rating"
        />
      </View>
      <View style={styles.fieldContainer}>
        <FormikTextInput placeholder="Comment" name="comment" multiline />
      </View>

      <Button onPress={onSubmit}>Create a review</Button>
    </View>
  );
};

const CreateReview = () => {

  const history = useHistory();
  const { id } = useParams();

  console.log('id: ', id);

  firebase.firestore().collection('users').doc(firebase.auth().currentUser.uid).collection('userinfo')
  .get().then((querySnapshot) => { querySnapshot.forEach((doc) => {  }); });


  const onSubmit = async (values) => {

    const { nosing, taste, finish, rating, comment } = values;

    const currentUser = firebase.auth().currentUser.uid;
    const currentUserName = 

    firebase.firestore().collection('users').doc(currentUser).collection('userinfo')
      .get().then((querySnapshot) => { querySnapshot.forEach((doc) => { console.log(doc.data()); }); });

    firebase.firestore().collection('whiskies').doc(id).collection('reviews')
      .add({
        // firstName:
        // lastName:  
        nosing: nosing,
        taste: taste,
        finish: finish,
        rating: rating,
        comment: comment
      });

    // firebase.firestore().collection('whiskies').doc(id).get().
    //   then((doc) => {
    //     firebase.firestore.collection('whiskies').doc(id).update({
    //       reviewCount: doc.reviewCount + 1,
    //       ratingAverage: (doc.ratingAverage + rating) / doc.reviewCount + 1
    //     }).catch((error) => {
    //       console.log("Error: ", error);
    //     });
    //   });


    history.push('/');

  };


  return (
    <View style={styles.container}>
      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        {({ handleSubmit }) => <CreateReviewForm onSubmit={handleSubmit} />}
      </Formik>
    </View>
  );
};

export default CreateReview;
