import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View } from 'react-native';
import { Button, Avatar, Divider } from 'react-native-paper';
import { useHistory } from 'react-router-native';

import firebase from "firebase/app";
import "firebase/firebase-firestore";
import "firebase/firebase-auth";
import uuid from 'uuid';

import { StateContext } from '../state';
import Text from './Text';

const styles = StyleSheet.create({
  topContainer: {
    // backgroundColor: 'blue',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  container: {
    // backgroundColor: 'red',
    flexDirection: 'row',
    alignItems: 'center',
  }, 
  bottomContainer: {

    // backgroundColor: 'green'
  },
  avatar: {
    marginBottom: 50,
  },
  button: {
    marginHorizontal: 50
  },
  button2: {
    marginTop: 20
  }
});


const Userpage = async () => {

  const history = useHistory();

  const url = await firebase.firestore()
  .collection('images')
  .doc(firebase.auth().currentUser.uid)
  .collection('userImages').get();


  return (
      <View style={styles.topContainer}>
      <Avatar.Image size={150} style={styles.avatar} source={url} />
        <View style={styles.container}>
          <Button icon="settings-outline" mode="outlined" compact="true" style={styles.button}>
            Settings
          </Button>

          <Button icon="lead-pencil" mode="outlined" compact="true" style={styles.button} onPress={() => history.push('/my-info')}>
            My info
          </Button>

        </View>
        <View style={styles.bottomContainer}>
          <Button icon="camera" mode="outlined" compact="true" style={styles.button2} onPress={() => history.push("/upload-media")}>
            Upload media
        </Button>
        </View>
      </View>
  );
};

export default Userpage;
