import React, { useState, useContext, useRef, useEffect, useCallback } from 'react';
import {View, StyleSheet} from 'react-native';
import {Button, TextInput} from 'react-native-paper';
import * as firebase from 'firebase';
import { sendChatMessage } from '../firebase/chat';
import { GiftedChat } from 'react-native-gifted-chat'


const styles = StyleSheet.create({
    container: {
        backgroundColor: 'red',
        flexDirection: 'column',
        height: '100%',
        width: '100%',
        position: 'absolute',
        zIndex: 1
    },
    messengerContainer: {

    },
    messageContainerSend: {
        backgroundcolor: 'red'

    },
    messageContainerReceived: {
        backgroundcolor: 'yellow'
    },
    sendMessageContainer: {
        flexDirection: 'row'
    }
});



const Messages = ({otherUser}) => {

    const [messages, setMessages] = useState([]);

    const [text, setText] = useState('');
    const currentUser = firebase.auth().currentUser;

    const chatID = () => {
        const chatterID = currentUser.uid;
        const chateeID = otherUser.uid;
        const chatIDpre = [];
        chatIDpre.push(chatterID);
        chatIDpre.push(chateeID);
        chatIDpre.sort();
        return chatIDpre.join('_');
      };


    // const db = firebase.firestore();
    //   db.collection('messages')
    //     .doc(chatID)
    //     .collection('chats')
    //     .orderBy('createdAt', 'desc')
    //     .onSnapshot((doc) => {
    //         setMessages(doc.docs);
    //         console.log(doc.docs);         
    //     });

    const onSend = useCallback((messages = []) => {
        setMessages(previousMessages => GiftedChat.append(previousMessages, messages));
    }, []);

    return(
        <View style={styles.container}>

            <GiftedChat
                messages={messages}
                onSend={messages => onSend(messages)}
                user={{
                    _id: 1
                }}
            />

            <View style={styles.sendMessageContainer}>
                <TextInput
                    label=""
                    value={text}
                    onChangeText={text => setText(text)}
                />
                <Button icon="send" onPress={()=>{sendChatMessage(chatID,text);}} />
            </View>

        </View>
    );
};

export default Messages;
