import React, { useState, useContext, useRef, useEffect, useCallback } from 'react';
import {View, StyleSheet} from 'react-native';
import {Button, IconButton, TextInput} from 'react-native-paper';
import * as firebase from 'firebase';
import { sendChatMessage, handleSend } from '../firebase/chat';
import { GiftedChat } from 'react-native-gifted-chat';
import { useParams } from 'react-router-native';
import Text from './Text';


const styles = StyleSheet.create({
    container: {
        zIndex: 1,
        padding: 100,
        height: '100%',
        width: '100%',
        position: 'absolute',
        flexDirection: 'column'
    },
    messengerContainer: {

    },
    messageContainerSend: {
        // backgroundcolor: 'red'

    },
    messageContainerReceived: {
        // backgroundcolor: 'yellow'
    },
    sendMessageContainer: {
        flexDirection: 'row'
    }
});



const Messenger = () => {

    const [messages, setMessages] = useState([]);

    const [text, setText] = useState('');
    const currentUser = firebase.auth().currentUser;
    const { id } = useParams();


    const chatID = () => {
        const chatterID = currentUser.uid;
        const chateeID = id;
        const chatIDpre = [];
        chatIDpre.push(chatterID);
        chatIDpre.push(chateeID);
        chatIDpre.sort();
        return chatIDpre.join('_');
      };

    useEffect(() => {
        const messagesListener = firebase.firestore()
            .collection('messages')
            .doc(chatID())
            .collection('chats')
            .orderBy('createdAt', 'desc')
            .onSnapshot(querySnapshot => {
                const messages = querySnapshot.docs.map(doc => {
                    const firebaseData = doc.data();

                    const data = {
                        _id: doc.id,
                        text: '',
                        createdAt: new Date().getTime(),
                        ...firebaseData
                    };

                    if (!firebaseData.system) {
                        data.user = {
                            ...firebaseData.user,
                            name: firebaseData.user.email
                        };
                    }

                    return data;
                });

                setMessages(messages);
            });

        return () => messagesListener();
    }, []);

    // const db = firebase.firestore();
    //   let getMessages = 
    //   db.collection('messages')
    //     .doc(chatID())
    //     .collection('chats')
    //     .orderBy('createdAt', 'desc')

    //     .onSnapshot((doc) => {
    //         setMessages(doc.docs);        
    //     });

    //     getMessages();

    // useEffect(() => {
    //     setMessages([
    //         {
    //             _id: 1,
    //             text: 'Hello developer',
    //             createdAt: new Date(),
    //             user: {
    //                 _id: 2,
    //                 name: 'React Native',
    //                 avatar: 'https://placeimg.com/140/140/any',
    //             },
    //         },
    //     ]);
    // }, []);

    // talleta messaget firestoreen ja get them from there

    // const onSend = useCallback((messages = []) => {
    //     setMessages(previousMessages => GiftedChat.append(previousMessages, messages));
    // }, []);

       const onSend = useCallback((messages = []) => {
        handleSend(previousMessages => GiftedChat.append(previousMessages, messages));
    }, []);


    // handle send callback systeemi
    return(
        <View style={styles.container}>

            <GiftedChat
                messages={messages}
                onSend={messages => onSend(messages)}
                // onSend={handleSend(chatID(), messages)}
                user={{
                    _id: currentUser.uid
                }}
            />

            {/* <View style={styles.sendMessageContainer}>
                <TextInput
                    label=""
                    value={text}
                    onChangeText={text => setText(text)}
                />
                <IconButton icon="send" size={25} onPress={()=>{sendChatMessage(chatID(),text);}} />
            </View> */}

        </View>
    );
};

export default Messenger;
