import React, { useState, useEffect } from 'react';
import { StyleSheet, View, ScrollView, Image } from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useHistory } from 'react-router-native';
import uuid from 'uuid';
import * as ImagePicker from 'expo-image-picker';
import { Button} from 'react-native-paper';
import Text from './Text';
import FormikTextInput from './FormikTextInput';

import * as firebase from 'firebase';


const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  fieldContainer: {
    margin: 5,
  },
  heading: {
    alignItems: 'center',
    marginBottom: 30
  },
  buttoncontainer: {

  }
});

const initialValues = {
  name: '',
  distillery: '',
  country: '',
  type: '',
  age: '',
  abv: '',
  bottlesize: ''

};

const validationSchema = yup.object().shape({
  name: yup
    .string()
    .required('Name is required'),
  distillery: yup
    .string()
    .required('Distillery is required'),
  country: yup
    .string()
    .required('Country is required'),
  type: yup
    .string()
    .required('Type is required'),
  age: yup 
    .number()
    .required('Age is required'),
  abv: yup
    .number()
    .required('Abv is required'),
  bottlesize: yup
    .number()
    .required('Bottlesize is required')
});



const UploadWhiskyForm = ({ onSubmit }) => {
  return (
    <View>
      <View style={styles.fieldContainer}>
        <FormikTextInput 
        name="name" 
        placeholder="Name" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="distillery"
          placeholder="Distillery"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="country"
          placeholder="Country"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="type"
          placeholder="Type"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="age"
          keyboardType="numeric"
          placeholder="Age"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="abv"
          keyboardType="numeric"
          placeholder="Abv"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="bottlesize"
          keyboardType="numeric"
          placeholder="Bottlesize"
        />
      </View>

      <Button icon={'bottle-wine'} style={styles.fieldContainer} mode='outlined' onPress={onSubmit}>
          Upload whisky
      </Button>
    </View>
  );
};



const UploadWhisky = () => {
  const history = useHistory();
  const [image, setImage] = useState();
  const [visible, setVisible] = useState(false);
  const [whiskyID, setWhiskyID] = useState('');
  const onToggleSnackBar = () => setVisible(!visible);



  const addImageToFirestore = async (downloadURL) => { 
    firebase.firestore()
    .collection('whiskyImages')
    .doc(whiskyID)
    .set({
      downloadURL
    });
  };

  const pickProfilePicFromLibrary = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    setWhiskyID(uuid.v4());
    console.log('whisky id: ', whiskyID);

    if (!result.cancelled) {
      uploadImage(result.uri);
      setImage(result.uri);
      onToggleSnackBar();
    }
  };

  const uploadImage = async(uri) => {

    const response = await fetch(uri);
    const blob = await response.blob();

    const ref = firebase.storage().ref().child(`images/whiskies/${whiskyID}`);

    const uploadTask = ref.put(blob);

    uploadTask.on('state_changed', 
  (snapshot) => {
    // Observe state change events such as progress, pause, and resume
    // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    console.log('Upload is ' + progress + '% done');
    switch (snapshot.state) {
      case firebase.storage.TaskState.PAUSED: // or 'paused'
        console.log('Upload is paused');
        break;
      case firebase.storage.TaskState.RUNNING: // or 'running'
        console.log('Upload is running');
        break;
    }
  }, 
  (error) => {
    // Handle unsuccessful uploads
    switch (error.code) {
      case 'storage/unauthorized':
        // User doesn't have permission to access the object
        console.log(error.code);
        break;
      case 'storage/canceled':
        // User canceled the upload
        console.log(error.code);
        break;
      case 'storage/unknown':
        // Unknown error occurred, inspect error.serverResponse
        console.log(error.code);
        break;
    }
  },
  () => {
    // Handle successful uploads on complete
    // For instance, get the download URL: https://firebasestorage.googleapis.com/...
    uploadTask.snapshot.ref.getDownloadURL().then((downloadURL) => {
      console.log('File available at', downloadURL);
      addImageToFirestore(downloadURL);
    });
  });

  };


  const onSubmit = async (values) => {
    const {name, distillery, country, type, age ,abv, bottlesize} = values;

    firebase.firestore().collection('whiskies').doc(whiskyID).set({
      name: name,
      distillery: distillery,
      country: country,
      type: type,
      age: age,
      abv: abv,
      bottlesize: bottlesize,
    });
    history.push('/');
  };

  return (
    <View style={styles.container}>
      <ScrollView>
      <View style={styles.heading}>
        <Text color={'primary'} fontSize={'heading'} fontWeight={'bold'}>
          Upload Whisky
        </Text>
      </View>

      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        {({ handleSubmit }) => <UploadWhiskyForm onSubmit={handleSubmit} />}
      </Formik>
      <Button icon='camera' mode='outlined' onPress={pickProfilePicFromLibrary} style={styles.buttoncontainer}>Upload image</Button>
      {image && <Image source={{ uri: image }} style={{ width: 100, height: 100 }} />}
      </ScrollView>


    </View>
  );
};

export default UploadWhisky;
