import React from 'react';
import { FlatList, View, StyleSheet, Alert } from 'react-native';

import ReviewItem from './ReviewItem';


const styles = StyleSheet.create({
  reviewItemWrapper: {
    padding: 15,
    backgroundColor: 'white',
  },
  separator: {
    height: 10,
  },
  actionsContainer: {
    marginTop: 15,
    flexDirection: 'row',
  },
  actionButton: {
    flexGrow: 1,
    marginRight: 15,
  },
  lastActionButton: {
    marginRight: 0,
  },
});



const ReviewItemWithActions = ({ review, onDelete }) => {
  return (
    <View>
        <ReviewItem review={review}/>        
    </View>
  );
};

const ItemSeparator = () => <View style={styles.separator} />;

const MyReviews = () => {
  const { authorizedUser, refetch } = useAuthorizedUser({
    includeReviews: true,
  });

  const reviewEdges = authorizedUser ? authorizedUser.reviews.edges : [];
  const reviewNodes = reviewEdges.map(({ node }) => node);

  const onDelete = async (id) => {
    console.log('deleted: ', id);
  };

  return (
    <FlatList
      data={reviewNodes}
      renderItem={({ item }) => (
        <ReviewItemWithActions
          review={item}
          onDelete={() => onDelete(item.id)}
        />
      )}
      keyExtractor={({ id }) => id}
      ItemSeparatorComponent={ItemSeparator}
    />
  );
};

export default MyReviews;
