import React , {useState, useEffect} from 'react';
import { FlatList, View, StyleSheet, Alert } from 'react-native';
import { Button, Snackbar } from 'react-native-paper';
import { Link } from 'react-router-native';
// import * as firebase from 'firebase';

import firebase from "firebase/app";
import "firebase/auth";
import "firebase/firestore";
import ReviewItem from './ReviewItem';


const styles = StyleSheet.create({
container: {
    flexDirection: 'column',
    zIndex: 1,
    // position: 'absolute',
    height: '100%',
    width: '100%',
    // paddingTop: 80,
    paddingBottom: 60
},
  reviewItemWrapper: {
    padding: 15,
    backgroundColor: '#FFD700',
  },
  separator: {
    height: 10,
  },
  actionsContainer: {
    marginTop: 15,
    borderWidth: 3,
    flexDirection: 'row',
  },
  actionButton: {
    marginTop: 5

  },
  lastActionButton: {

  },
});

const DeleteReviewButton = ({ onPress }) => {
    const alertButtons = [
      {
        text: 'Cancel',
        style: 'cancel',
      },
      {
        text: 'Delete',
        onPress: () => onPress(),
      },
    ];

    const deleteWithConfirmation = () => {
      Alert.alert(
        'Delete review',
        'Are you sure you want to delete this review?',
        alertButtons,
        { cancelable: false },
      );
    };

    return (
      <Button onPress={deleteWithConfirmation}>
        Delete review
      </Button>
    );
  };



const ReviewItemWithActions = ({ review, onDelete }) => {

  console.log('review: ', review.id);

  return (
    <View style={styles.reviewItemWrapper}>
        <ReviewItem review={review}/> 
        <Link
          component={Button}
          to={`/whiskies/${review.id}`}
          style={styles.actionButton}
        >
          View whisky
        </Link>
        <DeleteReviewButton
          onPress={onDelete}
          style={[styles.actionButton, styles.lastActionButton]}
        /> 
    </View>
  );
};

const ItemSeparator = () => <View style={styles.separator} />;

const MyReviews = () => {

    const [reviews, setReviews] = useState([]);
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        {firebase.auth().currentUser&&firebase.firestore()
            .collection('reviews').doc(firebase.auth().currentUser.uid).collection('userReviews')
            .get()
            .then((querySnapshot) => {
                let posts = querySnapshot.docs.map(doc => {
                    const data = doc.data();
                    const id = doc.id;
                    return { id, ...data };
                });
                setReviews(posts);
            })
            .catch((error) => {
                console.log("Error getting documents: ", error);
            });
}}, []);
 // use transaction instead
  const onDelete = async (id) => {

    console.log('pressed delete');

    let batch = firebase.firestore().batch();

    let reviewRef1 = firebase.firestore().collection('reviews').doc(firebase.auth().currentUser.uid).collection('userReviews')
      .doc(id);
    batch.delete(reviewRef1);

    let reviewRef2 = firebase.firestore().collection('whiskies').doc(id)
      .collection('reviews').doc(firebase.auth().currentUser.uid);
    batch.delete(reviewRef2);

    batch.commit().then(() => { setVisible(true); }).catch((error) => { console.log(error); });

    let rewDocRef = firebase.firestore().collection('whiskies').doc(id);

    firebase.firestore().runTransaction((transaction) => {
      // This code may get re-run multiple times if there are conflicts.
      return transaction.get(rewDocRef).then((rewDoc) => {
        if (!rewDoc.exists) {
          throw "Document does not exist!";
        }
        let newReviewCount = rewDoc.data().reviewCount - 1;
        let myRating = rewDoc.data().reviews.firebase.auth().currentUser.uid.rating;

        let newRating = (rewDoc.data().rating - myRating) / (newReviewCount);
        transaction.update(rewDocRef, { reviewCount: newReviewCount, rating: newRating });
      });
    }).then(() => {
      console.log("Transaction successfully committed!");
    }).catch((error) => {
      console.log("Transaction failed: ", error);
    });

  };

  return (
      <>
      <View style={styles.container}>
      <Snackbar visible={visible}>
            Review deleted
        </Snackbar>
          <FlatList
              data={reviews}
              renderItem={({ item }) => (
                  <ReviewItemWithActions
                      review={item}
                      onDelete={() => onDelete(item.id)}
                  />
              )}
              keyExtractor={({ id }) => id}
              ItemSeparatorComponent={ItemSeparator}
          />
      </View>
      </>
  );
};

export default MyReviews;
