import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View } from 'react-native';
import { Button, Avatar, Divider } from 'react-native-paper';

import { StateContext } from '../state';
import Text from './Text';

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  }, 
  avatar: {
    // marginBottom: 20
  },
  buttonContainer: {
    flexDirection: 'row',
  },
  button: {
    margin: 20
  }
});


const Userpage = () => {


  return (

    <View style={styles.container}>

      <Button icon="settings-outline" mode="outlined" compact="true" style={styles.button}>
        Settings
        </Button>

      <Avatar.Image size={100} style={styles.avatar} />

      <Button icon="lead-pencil" mode="outlined" compact="true" style={styles.button}>
        My info
        </Button>

      <View style={styles.buttonContainer}>

        <Button icon="camera" mode="outlined" compact="true" style={styles.button}>
          Upload media
        </Button>

      </View>
    </View>

  );
};

export default Userpage;
