import React from 'react';
import { View, Image, StyleSheet } from 'react-native';
import Text from './Text';
import formatInThousands from '../utils/formatInThousands';

const styles = StyleSheet.create({
  container: {
    padding: 5,
    flexDirection: 'row',
  },
  topContainer: {
    flexGrow: 1,
    backgroundColor: 'green'
  },
  middleContainer: {
    flexGrow: 0,
    backgroundColor: 'brown'

  },
  bottomContainer: {
    flexGrow: 0,
    backgroundColor: 'blue',
    justifyContent: 'space-around',
    flexDirection: 'row',
  },
  avatarContainer: {
    marginRight: 20,
  },
  contentContainer: {
    flexGrow: 1,
    // flexDirection: 'row',
  },
  nameText: {
    marginBottom: 5,
  },
  avatar: {
    width: 60,
    height: 120,
  },
  countItem: {
    flexGrow: 0,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 15,
  },
  countItemCount: {
    marginBottom: 5,
  },
});

const CountItem = ({ label, count, ...props }) => {
  return (
    <View style={styles.countItem}>
      <Text style={styles.countItemCount} fontWeight="bold" {...props}>
        {formatInThousands(count)}
      </Text>
      <Text color="textSecondary">{label}</Text>
    </View>
  );
};

const WhiskyItem = ({ whisky, ...props }) => {
  const {
    brand,
    nameAddition,
    age,
    distillationDate,
    bottlingDate,
    abv,
    bottlesize,
    downloadURL,
    reviewCount,
    ratingAverage,
    bottler,
  } = whisky;

  return (
    <View style={styles.container} {...props}>

        <View style={styles.avatarContainer}>
          <Image source={{ uri: downloadURL}} style={styles.avatar} />
        </View>
        <View style={styles.topContainer}>
        <View style={styles.contentContainer}>
          <Text
            style={styles.nameText}
            testID="whiskyItemName"
            fontSize='subheading'
          >
            {brand}{' '}{age}{' '}{distillationDate}{' '}{bottlingDate}
            {' '}{nameAddition}{' '}{abv}{' '}{bottlesize}{'L'}
          </Text>
          {/* <Text
            style={styles.nameText}
            testID="whiskyItemBrand"
            fontSize='subheading'
          >
            {brand}{' '}
          </Text>
          {!!age&&<Text
            style={styles.nameText}
            fontSize='subheading'
            testID="whiksyItemAge"
          >
            {age} YO{' '}
          </Text>}
          {!!distillationDate&&<Text
            style={styles.nameText}       
            fontSize='subheading'     
            testID="whiksyItemDistillationDate"
          >
            {distillationDate}{'/'}
          </Text>}
          {!!bottlingDate&&<Text
            style={styles.nameText}
            fontSize='subheading'
            testID="whiksyItemBottlingDate"
          >
            {bottlingDate}{' '}
          </Text>}
          {!!nameAddition&&<Text
            style={styles.nameText}
            fontSize='subheading'
            testID="whiksyItemBottlingDate"
          >
            {nameAddition}{' '}
          </Text>}
          <Text
            style={styles.nameText}
            fontSize='subheading'
            testID="whiskyItemAbv"
          >
            {abv}%{' '}
          </Text>
          <Text
            style={styles.nameText}
            fontSize='subheading'
            testID="whiskyItemBottlesize"
          >
            {bottlesize}L{' '}
          </Text> */}
        </View>
        <View style={styles.middleContainer}>
        <Text
            style={styles.nameText}
            fontSize='subheading'
            testID="whiskyItemBottlesize"
          >
            {bottler}{' '}
            </Text>
        </View>
        <View style={styles.bottomContainer}>
        <CountItem
          count={ratingAverage}
          label="Rating"
          testID="repositoryItemRating"
        />
        <CountItem
          count={reviewCount}
          label="Reviews"
          testID="repositoryItemReviews"
        />
      </View>
      </View>

    </View>
  );
};

export default WhiskyItem;
