import React , {useState, useEffect} from 'react';
import { FlatList, View, StyleSheet, Alert } from 'react-native';
import { Button, Snackbar } from 'react-native-paper';
import { Link } from 'react-router-native';
import * as firebase from 'firebase';
import WhiskyItem from './WhiskyItem';


const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    zIndex: 1,
    // position: 'absolute',
    height: '100%',
    width: '100%',
    // paddingTop: 80,
    paddingBottom: 60
},
  CollectionItemWrapper: {
    padding: 15,
    backgroundColor: 'orange',
  },
  separator: {
    height: 10,
  },
  actionsContainer: {
    marginTop: 15,
    flexDirection: 'row',
  },
  actionButton: {
    flexGrow: 1,
    marginRight: 15,
  },
  lastActionButton: {
    marginRight: 0,
  },
});

const DeleteCollectionButton = ({ onPress }) => {
    const alertButtons = [
      {
        text: 'Cancel',
        style: 'cancel',
      },
      {
        text: 'Delete',
        onPress: () => onPress(),
      },
    ];

    const deleteWithConfirmation = () => {
      Alert.alert(
        'Delete review',
        'Are you sure you want to delete this review?',
        alertButtons,
        { cancelable: false },
      );
    };

    return (
      <Button onPress={deleteWithConfirmation} mode='outlined'>
        Delete whisky
      </Button>
    );
  };



const CollectionItemWithActions = ({ whisky, onDelete }) => {

  console.log('whiskyID: ', whisky.whiskyID);

  return (
    <View style={styles.CollectionItemWrapper}>
        <WhiskyItem whisky={whisky}/> 
        <Link
          component={Button}
          to={`/whiskies/${whisky.whiskyID}`}
          style={styles.actionButton}
        >
          View whisky
        </Link>
        <DeleteCollectionButton
          onPress={onDelete}
          style={[styles.actionButton, styles.lastActionButton]}
        /> 
    </View>
  );
};

const ItemSeparator = () => <View style={styles.separator} />;

const MyCollection = () => {

    const [myCollection, setMyCollection] = useState([]);
    const [visible, setVisible] = useState(false);

    console.log('myCollection: ', myCollection);

    useEffect(() => {
        {firebase.auth().currentUser&&firebase.firestore()
            .collection('collections')
            .get()
            .then((querySnapshot) => {
                let posts = querySnapshot.docs.map(doc => {
                    const data = doc.data();
                    const id = doc.id;
                    return { id, ...data };
                });
                setMyCollection(posts);
            })
            .catch((error) => {
                console.log("Error getting documents: ", error);
            });
}}, []);

  const onDelete = async (id) => {
    // use transaction
    firebase.firestore().collection('reviews').doc(firebase.auth().currentUser.uid).collection('userReviews')
    .doc(id).delete().then(() => {setVisible(true);}).catch((error) => {console.log(error);});

  };

  return (
    <>
      <View style={styles.container}>
        <Snackbar visible={visible}>
          Review deleted
        </Snackbar>
        <FlatList
          data={myCollection}
          renderItem={({ item }) => (
            <CollectionItemWithActions
              whisky={item}
              onDelete={() => onDelete(item.id)}
            />
          )}
          keyExtractor={({ id }) => id}
          ItemSeparatorComponent={ItemSeparator}
        />
      </View>
    </>
  );
};

export default MyCollection;
