import firebase from "firebase/app";
import "firebase/firestore";



export const sendChatMessage = async (chatID, chat) => {
  console.log('chatid: ', chatID);
  console.log('chat: ', chat);
  firebase.firestore()
    .collection('messages')
    .doc(chatID)
    .collection('chats')
    .add(chat);
};
