import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Image, ActivityIndicator,  useWindowDimensions } from 'react-native';
import { Button, Avatar, Divider } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import { TabView, SceneMap } from 'react-native-tab-view';

import { StateContext } from '../state';
import Text from './Text';

import firebase from "firebase/app";
import "firebase/auth";
import "firebase/firestore";

import theme from '../theme';

const styles = StyleSheet.create({
  topContainer: {
    // flexDirection: 'column',
    // justifyContent: 'center',
    // alignItems: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  container: {
    // flexDirection: 'row',
    alignItems: 'center',
  }, 
  bottomContainer: {

  },
  avatar: {
    marginBottom: 50,
    width: 150, 
    height: 150,
    borderRadius: 100
  },
  loading: {
    position: 'absolute',
    top: 245,
    zIndex: 2
  },
  button: {
    marginHorizontal: 50
  },
  button2: {
    marginTop: 20
  },
});


const FirstRoute = () => (
  <View style={{ flex: 1, backgroundColor: 'yellow' }} />
);

const SecondRoute = () => (
  <View style={{ flex: 1, backgroundColor: 'red' }} />
);

const renderScene = SceneMap({
  first: FirstRoute,
  second: SecondRoute,
});




const Userpage = () => {

  const history = useHistory();
  const [img, setImg] = useState();
  const [loaded, setLoaded] = useState(false);


  const layout = useWindowDimensions();

  const [index, setIndex] = React.useState(0);
  const [routes] = React.useState([
    { key: 'first', title: 'First' },
    { key: 'second', title: 'Second' },
  ]);



  useEffect(() => {


    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .doc("profilePicture")
    .get().then((snapshot) => {
      setImg(snapshot.data().downloadURL);
    });

  },[]);

  const linkTo = (link) => {
    history.push(link);
  };


  return (

      <View style={styles.topContainer}>
      {/* <View style={styles.loading}>
        <ActivityIndicator size='large' animating={!loaded} color={theme.colors.primary} />
      </View>
      <Image source={{ uri: img }} style={styles.avatar} onLoadEnd={() => setLoaded(true)} defaultSource={''}/>
      <View style={styles.container}>
        <Button icon="star" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-reviews')}>
         My reviews
          </Button>

        <Button icon="information" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-info')}>
          My info
          </Button>

          <Button icon="database" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-collection')}>
          My collection
          </Button>

          <Button icon="glass-tulip" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-wishList')}>
          My wish List
          </Button> */}
      <View style={styles.container}>

        <TabView
          navigationState={{ index, routes }}
          renderScene={renderScene}
          onIndexChange={setIndex}
          initialLayout={{ width: layout.width }}
        />
      </View>



      {/* </View> */}
      </View>

  );
};

export default Userpage;
