import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, ScrollView, Image, FlatList} from 'react-native';
import { Button, Avatar, Divider, IconButton  } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import * as firebase from 'firebase';
import { array } from 'yup';
import { GET_USERINFO } from '../firebase/queries';
import { StateContext } from '../state';


const styles = StyleSheet.create({

  container: {
    position: 'absolute',
    top: 100

  },
  containerGaller: {

  }, 
  containerImage: {
    padding: 10

  },
  image: {
    width: 50,
    height: 50
  }


});

const getUserInfo = async () => {

  try {
    GET_USERINFO();
  } catch (err) {
    console.log(err);
  }
};



const MyInfo = () => {

  const [files, setFiles] = useState([]);

  // tsekkaa miten aiemmin teit

  useEffect(() => {
    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .get()
      .then((querySnapshot) => {
        let posts = querySnapshot.docs.map(doc => {
          const data = doc.data();
          const id = doc.id;
          return { id, ...data };
      });

        setFiles(posts);
      })
      .catch((error) => {
        console.log("Error getting documents: ", error);
      });

  },[]);

  getUserInfo();



  return (
    <View style={styles.container}>
      <View style={styles.containerGallery}>
        <FlatList
          numColumns={3}
          horizontal={false}
          data={files}
          renderItem={({ item }) => (
            <View
              style={styles.containerImage}>

              <Image
                style={styles.image}
                source={{ uri: item.downloadURL }}
              />
            </View>

          )}
          keyExtractor={({ id }) => id}
        />
      </View>

    </View>
  );
};

export default MyInfo;
