import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Image, ActivityIndicator,  useWindowDimensions, ImageBackground } from 'react-native';
import { Button, Avatar, Divider } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import { TabView, SceneMap } from 'react-native-tab-view';

import { StateContext } from '../state';
import Text from './Text';

import MyReviews from './MyReviews';
import MyCollection from './MyCollection';
import MyWishList from './MyWishList';

import firebase from "firebase/app";
import "firebase/auth";
import "firebase/firestore";

import theme from '../theme';


const styles = StyleSheet.create({
  topContainer: {
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    // height: '100%',
    // width: '100%',
    flexGrow: 1,
    // position: 'absolute',

  },
  scrollView: {
    backgroundColor: 'pink'
  },  
  bottomContainer: {
    // height: '100%',
    // width: '100%',
    flexGrow: 4
  },
  container: {
    zIndex: 1,
    height: '100%',
    width: '100%',
    flexDirection: 'column',
    position: 'absolute',
    backgroundColor: 'yellow'
    // alignItems: 'center',
  }, 
  avatar: {
    top: 20,
    width: 100, 
    height: 100,
    borderRadius: 100
  },
  loading: {
    top: 90,
    zIndex: 2
  },
  button: {
    marginHorizontal: 50
  },
  button2: {
    marginTop: 20
  },
});



const renderScene = SceneMap({
  first: MyReviews,
  second: MyCollection,
  third: MyWishList
});




const Userpage = () => {

  const [img, setImg] = useState();
  const [loaded, setLoaded] = useState(false);
  const [userinfo, setUserinfo] = useState([]);

  const layout = useWindowDimensions();

  const [index, setIndex] = useState(0);
  const [routes] = useState([
    { key: 'first', title: 'My Reviews' },
    { key: 'second', title: 'My Collection' },
    { key: 'third', title: 'My Wish List' }
  ]);



  useEffect(() => {

    firebase.firestore().collection('userinfo').doc(firebase.auth().currentUser.uid)
    .get().then((doc) => {setUserinfo(doc.data());});

    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .doc("profilePicture")
    .get().then((snapshot) => {
      setImg(snapshot.data().downloadURL);
    });

  },[]);

  return (
    <View style={styles.container}>

      <View style={styles.topContainer}>

      <View style={styles.loading}>
        <ActivityIndicator size='large' animating={!loaded} color={theme.colors.primary} />
      </View>
      <Image source={{ uri: img }} style={styles.avatar} onLoadEnd={() => setLoaded(true)} defaultSource={''} />
      </View>

      <View style={styles.bottomContainer}>

        <TabView
          navigationState={{ index, routes }}
          renderScene={renderScene}
          onIndexChange={setIndex}
          initialLayout={{ width: layout.width }}
        />
      </View>

      </View>

  );
};

export default Userpage;
