import React, { useContext , useRef} from 'react';

import {
  View,
  ScrollView,
  StyleSheet,
  // TouchableWithoutFeedback
  Animated
} from 'react-native';

import Constants from 'expo-constants';
import { Link } from 'react-router-native';
import { useApolloClient } from '@apollo/react-hooks';
import { useHistory } from 'react-router-native';

import theme from '../theme';
import Text from './Text';
import AuthStorageContext from '../contexts/AuthStorageContext';
import useAuthorizedUser from '../hooks/useAuthorizedUser';

import Button from './Button';
import { IconButton } from 'react-native-paper';
import { StateContext } from '../state';
import SideMenu from './SideMenu';

import moveMenu from './SideMenu';


const styles = StyleSheet.create({
  container: {
    paddingTop: Constants.statusBarHeight,
    backgroundColor: theme.colors.appBarBackground,
  },
  scrollView: {
    flexDirection: 'row',
  },
  tabTouchable: {
    flexGrow: 0,
  },
  tabContainer: {
    paddingHorizontal: 15,
    paddingVertical: 20,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  tabText: {
    color: 'white',
  }
});

// const AppBarTab = ({ children, ...props }) => { 
//   return (
//     <TouchableWithoutFeedback style={styles.tabTouchable} {...props}>
//       <View style={styles.tabContainer}>
//         <Text fontWeight="bold" style={styles.tabText}>
//           {children}
//         </Text>
//       </View>
//     </TouchableWithoutFeedback>
//   );
// };

const AppBarTab = ({ children, ...props }) => { 
  // on press {...props}
  return (
  <View style={styles.tabContainer}>
      <Button {...props} >{children}</Button> 
  </View>

  );
};


const AppBar = () => {
  const apolloClient = useApolloClient();
  const authStorage = useContext(AuthStorageContext);
  const history = useHistory();

  const { authorizedUser } = useAuthorizedUser();

  const { state, dispatch } = useContext(StateContext);

  const toggleAnim = useRef(new Animated.Value(0)).current; // tämä ei muutu

  const onSignOut = async () => {
    await authStorage.removeAccessToken();
    apolloClient.resetStore();
    history.push('/');
  };

  console.log('open: ', state.open);

  return (


    <View style={styles.container}>
      <ScrollView style={styles.scrollView} horizontal> 
        <IconButton icon="menu" color={theme.colors.primary} size={40} onPress={() => dispatch({type: "SET_OPEN", payload: !state.open}, moveMenu({state, toggleAnim}))} 
        ></IconButton>
        {authorizedUser ? (
          <>
            <AppBarTab onPress={onSignOut}>Sign out</AppBarTab>
          </>
        ) : (
          <>
            <Link to="/sign-in" component={AppBarTab}>
              Sign in
            </Link>
            <Link to="/sign-up" component={AppBarTab}>
              Sign up
            </Link>
            <Link to="/" component={AppBarTab}>
              Home
            </Link>
          </>
        )}
      </ScrollView>
    </View>
  );
};

export default AppBar;
