import * as firebase from 'firebase';

export const sendChatMessage = (chatID, text, otherUserUid) => {

  firebase.firestore()
    .collection('messages')
    .doc(chatID)
    .collection('chats')
    .add({
      text: text,
      createdAt: new Date().getTime(),
      user: {
        _id: firebase.auth().currentUser.uid
      }
    });

    firebase.firestore()
    .collection('messages')
    .add({
      text: text,
      createdAt: new Date().getTime(),
      sendFrom: firebase.auth().currentUser.uid,
      sendTo: otherUserUid
    });    
};



