import React, { useContext, useEffect, useRef } from 'react';
import { Animated, StyleSheet, View } from 'react-native';
import { ToggleButton } from 'react-native-paper';
import { IconButton } from 'react-native-paper';

import { StateContext } from '../state';
import Button from './Button';
import theme from '../theme';
import Constants from 'expo-constants';

const styles = StyleSheet.create({ 
  sidebar: {
    width: 200,
    height: '100%', 
    backgroundColor: "blue",
    position: 'fixed',
    top: Constants.statusBarHeight.valueOf()
  },
  button: {
    padding: 10,
  },
  menuIcon: {
    position: 'absolute'
  }
});

const SideMenu = () => {
  const toggleAnim = useRef(new Animated.Value(0)).current; 
  const { state, dispatch } = useContext(StateContext);

  console.log('state: ', state.open);

  const moveMenu = () => {

    Animated.spring(
      toggleAnim,
      {
        toValue: state.open ? 0 : -200
      }
    ).start();

  };

  return (
    <>
       <IconButton icon="menu" color={theme.colors.primary} size={40} onPress={() => dispatch({type: "SET_OPEN", payload: !state.open}, moveMenu())} 
        ></IconButton>
      <Animated.View style={[styles.sidebar, {
        transform: [{
          translateX: toggleAnim
        }]
      }]}>
        <View style={styles.button}>
          <Button color='error' onPress={() => moveMenu()} >Button1</Button>
        </View>
      </Animated.View>

      {/* < SlideMenu toggleAnim={toggleAnim} state={state}/> */}
      {/* <SlidingMenu
        state={state.open}
        toggleAnim={toggleAnim}
      /> */}
    </>

  );
};

export default SideMenu;
