import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, ScrollView, Image, ActivityIndicator} from 'react-native';
import { Button, Avatar, Divider, IconButton  } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import * as firebase from 'firebase';
import Text from './Text';
import theme from '../theme';


const styles = StyleSheet.create({

  container: {
    flexDirection: 'column',
    // justifyContent: 'center',
    alignItems: 'center',
    height: '100%',
    width: '100%',
    // position: 'absolute',
    zIndex: 1

  },
  topContainer: {

  },
  text: {

  },
  separator: {
    height: 5,
  },
  avatarContainer: {
    height: 100,
    width: 100,
    backgroundColor: 'red'
  },
  avatar: {
    top: 30,
    width: 100, 
    height: 100,
    borderRadius: 100
  },
  loading: {
    backgroundColor: 'green',
    top: 90,
    zIndex: 2
  }


});




const MyInfo = () => {

  const [img, setImg] = useState();
  const [loaded, setLoaded] = useState(false);
  const [userinfo, setUserinfo] = useState([]);

  useEffect(() => {
    firebase.firestore().collection('userinfo').doc(firebase.auth().currentUser.uid)
    .get().then((doc) => {setUserinfo(doc.data());});

    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .doc("profilePicture")
    .get().then((snapshot) => {
      setImg(snapshot.data().downloadURL);
    });

  },[]);



  return (
    <View style={styles.container}>

      <View style={styles.topContainer}>

        <View style={styles.loading}>
          <ActivityIndicator size='large' animating={!loaded} color={theme.colors.primary} />
        </View>
        <Image source={{ uri: img }} style={styles.avatar} onLoadEnd={() => setLoaded(true)} defaultSource={''} />
      </View>

      <View style={styles.bottomContainer}>

        <View>
          <Text
            style={styles.normalText}
            fontSize="subheading"
          >
            Name: {userinfo.name}
          </Text>

          <Text
            style={styles.normalText}
            fontSize="subheading"
          >
            Birthdate: {userinfo.birthdate}
          </Text>


          <Text
            style={styles.bioText}
            fontSize="subheading"
          >
            Bio: {userinfo.bio}
          </Text>
        </View>
        <View>
          <Button mode='outlined'>Edit info</Button>
          <Button mode='outlined'>Change profile picture</Button>
        </View>



      </View>

    </View>
  );
};

export default MyInfo;
