import React, { useContext, useRef, useEffect, useState } from 'react';
import { Animated, StyleSheet, View, Image } from 'react-native';
import { useQuery } from '@apollo/client'; 

import { StateContext } from '../state';
import Text from './Text';
import fileService from '../services/files';

const styles = StyleSheet.create({
  home: {
    backgroundColor: "white",
    height: '100%',
    width: '100%',
    position: 'fixed',
    zIndex: 49,
    top: 60

  },
  photo: {
    width: 50,
    height: 50,
  },
});


const Home = () => {
  const homeAnim = useRef(new Animated.Value(0)).current; 
  const { state } = useContext(StateContext);
  const [files, setFiles] = useState([]);

  const moveMenu = () => {

    Animated.spring(
      homeAnim,
      {
        toValue: state.open ? 600 : 0
      }
    ).start();

  };

  useEffect(() => {
    moveMenu();
    fileService.getAll().then(files =>
      setFiles(files));
  },[]);



  return (
    <>
      {/* <Animated.View style={[styles.home, {
        transform: [{
          translateX: homeAnim
        }]
      }]}>
        <View>
          <Text>Homepage</Text>
        </View>
      </Animated.View> */}
      <View>
        {/* <Image
          style={styles.photo}
          source={{
            uri: 'https://reactnative.dev/img/tiny_logo.png',
          }}
        /> */}
        <Image 
        style={styles.photo}
        source={{
          uri: 'http://localhost:3001/image/982cdbcfd4503dd46b916f6ef002dd70.png'
          }}/>
      </View>
    </>

  );
};

export default Home;
