import React, { useState, useContext } from 'react';
import { FlatList, View, StyleSheet, TouchableOpacity } from 'react-native';
import { useHistory } from 'react-router-native';
import { Searchbar } from 'react-native-paper';
import { useDebounce } from 'use-debounce';

import RepositoryItem from './RepositoryItem';
import useRepositories from '../hooks/useRepositories';
import Picker from './Picker';
import { StateContext } from '../state';
import { Spring, animated } from 'react-spring/renderprops';


const styles = StyleSheet.create({
  sidebar: {
    backgroundColor: "blue",
    color: "#fff",
    position: "fixed",
    height: "100",
    width: "200",
    padding: "12",
    left: 'left',
    opacity: 'opacity'
    // position: 'relative',
    // overflow: 'hidden',
    // height: 100,
    // width: 280,
    // background: "#FFFFFF",
    // padding: 20,
    // fontSize: 15
  },
  separator: {
    height: 10,
  },
  headerContainer: {
    padding: 15,
  },
  searchContainer: {
    marginBottom: 15,
  },
});

const ItemSeparator = () => <View style={styles.separator} />;

const orderByOptions = [
  { label: 'Latest repositories', value: 'latest' },
  {
    label: 'Highest rated repositories',
    value: 'highestRating',
  },
  {
    label: 'Lowest rated repositories',
    value: 'lowestRating',
  },
];

const variablesByOrderBy = {
  latest: {
    orderBy: 'CREATED_AT',
    orderDirection: 'DESC',
  },
  highestRating: {
    orderBy: 'RATING_AVERAGE',
    orderDirection: 'DESC',
  },
  lowestRating: {
    orderBy: 'RATING_AVERAGE',
    orderDirection: 'ASC',
  },
};


const RepositoryListHeader = ({
  onOrderByChange,
  orderBy,
  searchKeyword,
  onSearchKeywordChange,
}) => {
  return (
    <View style={styles.headerContainer}>
      <View style={styles.searchContainer}>
        <Searchbar
          placeholder="Search repositories"
          value={searchKeyword}
          onChangeText={onSearchKeywordChange}
        />
      </View>
      <Picker
        placeholder={{}}
        onValueChange={onOrderByChange}
        value={orderBy}
        items={orderByOptions}
      />
    </View>
  );
};



export class RepositoryListContainer extends React.Component {

  renderHeader = () => {
    const {
      onOrderByChange,
      orderBy,
      searchKeyword,
      onSearchKeywordChange,
    } = this.props;

    return (
      <RepositoryListHeader
        onOrderByChange={onOrderByChange}
        orderBy={orderBy}
        searchKeyword={searchKeyword}
        onSearchKeywordChange={onSearchKeywordChange}
      />
    );
  };

  render() {


    const { state, repositories, onEndReach, onRepositoryPress } = this.props;

    console.log('state: ', state);

    const repositoryNodes = repositories
      ? repositories.edges.map((edge) => edge.node)
      : [];

    return (
      <>
      <View>
          <Spring
              from={{
                width: 100,
                padding: 0,
                background:
                  'linear-gradient(to right, #30e8bf, #ff8235)',
                transform:
                  'translate3d(400px,0,0) scale(2) rotateX(90deg)',
                boxShadow: '0px 100px 150px -10px #2D3747',
                borderBottom: '0px solid white',
                shape: 'M20,380 L380,380 L380,380 L200,20 L20,380 Z',
                textShadow: '0px 5px 0px white'
              }}
              to={{
                width: 'auto',
                padding: 20,
                background:
                  'linear-gradient(to right, #009fff, #ec2f4b)',
                transform:
                  'translate3d(0px,0,0) scale(1) rotateX(0deg)',
                boxShadow: '0px 10px 20px 0px rgba(0,0,0,0.4)',
                borderBottom: '10px solid #2D3747',
                shape: 'M20,20 L20,380 L380,380 L380,20 L20,20 Z',
                textShadow: '0px 5px 15px rgba(255,255,255,0.5)'
              }}>
            {props => <animated.div style={props}></animated.div>}
          </Spring>
      </View>


      <FlatList
        data={repositoryNodes}
        keyExtractor={({ id }) => id}
        renderItem={({ item }) => (
          <TouchableOpacity
            key={item.id}
            onPress={() => onRepositoryPress(item.id)}
          >
            <RepositoryItem repository={item} />
          </TouchableOpacity>
        )}
        ItemSeparatorComponent={ItemSeparator}
        ListHeaderComponent={this.renderHeader}
        onEndReached={onEndReach}
        onEndReachedThreshold={0.5}
        initialNumToRender={8}
      />
      </>

    );
  }
}

const RepositoryList = () => {
  const history = useHistory();
  const [orderBy, setOrderBy] = useState('latest');
  const [searchKeyword, setSearchKeyword] = useState('');
  const [debouncedSearchKeyword] = useDebounce(searchKeyword, 500);
  const { state } = useContext(StateContext);

  const { repositories, fetchMore } = useRepositories({
    first: 8,
    ...variablesByOrderBy[orderBy],
    searchKeyword: debouncedSearchKeyword,
  });

  const onEndReach = () => {
    fetchMore();
  };


  return (
    <RepositoryListContainer
      state={state.open}
      repositories={repositories}
      orderBy={orderBy}
      onOrderByChange={(newOrderBy) => {
        setOrderBy(newOrderBy);
      }}
      onEndReach={onEndReach}
      searchKeyword={searchKeyword}
      onSearchKeywordChange={(keyword) => setSearchKeyword(keyword)}
      onRepositoryPress={(id) => {
        history.push(`/repositories/${id}`);
      }}
    />
  );
};

export default RepositoryList;
