import React, {useState} from 'react';
import { StyleSheet, View } from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useHistory } from 'react-router-native';
import { useMutation } from '@apollo/react-hooks';
import { deleteUser, signInAnonymously, signInWithGoogle, signUpWithEmail } from '../firebase/firebaseFunctions';

import Button from './Button';
import FormikTextInput from './FormikTextInput';
import useSignIn from '../hooks/useSignIn';
import { CREATE_USER } from '../graphql/mutations';

import Text from './Text';

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    padding: 15,
  },
  fieldContainer: {
    marginBottom: 15,
  },
});

const initialValues = {
  username: '',
  password: '',
  passwordConfirmation: '',
};

const validationSchema = yup.object().shape({
  username: yup
    .string()
    .min(1, 'Name must be at least 1 character long')
    .max(30, 'Name must be at most 30 characters long')
    .required('Name is required'),
  email: yup
    .string()
    .required('Email is required'),
  password: yup
    .string()
    .min(5, 'Password must be at least 5 characters long')
    .max(50, 'Password must be at least 50 characters long')
    .required('Password is required'),
  passwordConfirmation: yup
    .string()
    .oneOf(
      [yup.ref('password'), null],
      'Password confirmation must match the password',
    )
    .required('Password confirmation is required'),
});

const SignUpForm = ({ onSubmit }) => {
  return (
    <View style={styles.container}>

      <View style={styles.fieldContainer}>
        <FormikTextInput name="email" placeholder="Email" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput name="username" placeholder="Username" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="password"
          placeholder="Password"
          secureTextEntry
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="passwordConfirmation"
          placeholder="Password confirmation"
          secureTextEntry
        />
      </View>

      <Button onPress={onSubmit} testID="submitButton">
        Sign up
      </Button>
    </View>
  );
};

const SignUp = () => {
  const history = useHistory();

  const onSubmit = async (values) => {
    const { email, username, password } = values;

    console.log(email, username, password);

    signUpWithEmail(email, username, password);
    // signInWithGoogle();
    // signInAnonymously();
      //  deleteUser();

    history.push('/');
  };

  return (

    <Formik
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
    >
      {({ handleSubmit }) => <SignUpForm onSubmit={handleSubmit} />}
    </Formik>
  );
};

export default SignUp;
