import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View } from 'react-native';
import { Button, Avatar, Divider } from 'react-native-paper';

import { StateContext } from '../state';
import Text from './Text';

const styles = StyleSheet.create({
  topContainer: {
    backgroundColor: 'blue',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  container: {
    backgroundColor: 'red',
    flexDirection: 'row',
    alignItems: 'center',
  }, 
  bottomContainer: {

    backgroundColor: 'green'
  },
  avatar: {
    // marginTop: 100,
    // alignSelf: 'flex-start' 
  },
  button: {
    margin: 50
  },
  button2: {
    marginTop: 20
  }
});


const Userpage = () => {


  return (
    <>
      <View style={styles.topContainer}>
      <Avatar.Image size={100} style={styles.avatar} />
        <View style={styles.container}>
          <Button icon="settings-outline" mode="outlined" compact="true" style={styles.button}>
            Settings
          </Button>

          {/* <Avatar.Image size={100} style={styles.avatar} /> */}

          <Button icon="lead-pencil" mode="outlined" compact="true" style={styles.button}>
            My info
          </Button>

        </View>
        <View style={styles.bottomContainer}>
          <Button icon="camera" mode="outlined" compact="true" style={styles.button2}>
            Upload media
        </Button>
        </View>
      </View>
    </>
  );
};

export default Userpage;
