import React, { useContext, useRef } from 'react';
import { Animated, StyleSheet, View } from 'react-native';

import { StateContext } from '../state';
import Button from './Button';

const styles = StyleSheet.create({
  sidebar: {
    width: 200,
    height: '100%', 
    backgroundColor: "blue",
    // position: 'fixed',
  },
  button: {
    padding: 10,
  }
});


export class SlidingMenu extends React.Component {

  state = {
    toggleAnim: new Animated.Value(0)
  };

  render() {

    const { state } = this.props;

    Animated.spring(
      this.state.toggleAnim,
      {
        toValue: state ? 0 : -200,
      }
    ).start();

    return (
      <Animated.View style={[styles.sidebar, {
        transform: [{
          translateX: this.state.toggleAnim
        }]
      }]}>
        <View style={styles.button}>
          <Button color='error' onPress={() => console.log('Button pressed')} >Button1</Button>
        </View>
        <View style={styles.button}>
          <Button color='error' onPress={() => console.log('Button pressed')} >Button2</Button>
        </View>
      </Animated.View>

    );
  }
}


const SideMenu = () => {
  const { state } = useContext(StateContext);
  const toggleAnim = useRef(new Animated.Value(0)).current;


  return (
    <>
      <SlidingMenu
        state={state.open}
        toggleAnim={toggleAnim}
      />
    </>

  );
};

export default SideMenu;
