import React from 'react';
import { View, Image, StyleSheet } from 'react-native';
import { Linking } from 'expo';

import theme from '../theme';
import Text from './Text';
import CustomButton from './CustomButton';
import formatInThousands from '../utils/formatInThousands';

const styles = StyleSheet.create({
    container: {
        zIndex: 1,
        height: '100%',
        width: '100%',
        position: 'absolute',
        paddingVertical: 80,
    },
    headingContainer: {
      flexDirection: 'column'  
    },
    topContainer: {
        flexDirection: 'row',
        marginBottom: 15,
    },
    bottomContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
    },
    avatarContainer: {
        flexGrow: 0,
        marginRight: 20,
    },
    contentContainer: {
        flexGrow: 1,
        flexShrink: 1,
    },
    headingText: {
        marginBottom: 5
    },  
    nameText: {
        marginBottom: 5,
    },
    descriptionText: {
        flexGrow: 1,
    },
    avatar: {
        width: 200,
        height: 200,
    },
    countItem: {
        flexGrow: 0,
        alignItems: 'center',
        justifyContent: 'center',
        paddingHorizontal: 15,
    },
    countItemCount: {
        marginBottom: 5,
    },
    languageContainer: {
        marginTop: 10,
        flexDirection: 'row',
    },
    languageText: {
        color: 'white',
        backgroundColor: theme.colors.primary,
        borderRadius: theme.roundness,
        flexGrow: 0,
        paddingVertical: 3,
        paddingHorizontal: 6,
    },
    githubButton: {
        marginTop: 15,
    },
});

// const CountItem = ({ label, count, ...props }) => {
//   return (
//     <View style={styles.countItem}>
//       <Text style={styles.countItemCount} fontWeight="bold" {...props}>
//         {formatInThousands(count)}
//       </Text>
//       <Text color="textSecondary">{label}</Text>
//     </View>
//   );
// };

const WhiskyItemInfo = ({ whisky, ...props }) => {
    const {
        brand,
        age,
        distillationDate,
        bottlingDate,
        nameAddition,
        abv,
        bottlesize,
        downloadURL,
        // reviewCount,
        // ratingAverage
    } = whisky;


    return (
        <View style={styles.container} {...props}>
            <View style={styles.topContainer}>
                <View style={styles.avatarContainer}>
                    <Image source={{ uri: downloadURL }} style={styles.avatar} />
                </View>
                <View style={styles.headingContainer}>
                    <Text
                        style={styles.headingText}
                        fontWeight="bold"
                        fontSize="subheading"
                        numberOfLines={1}
                        testID="whiskyItemBrand"
                    >
                        {brand}
                    </Text>
                    {age && <Text
                        style={styles.headingText}
                        color="textSecondary"
                        testID="whiksyItemAge"
                    >
                        {age} YO
                    </Text>}
                    {distillationDate && <Text
                        style={styles.headingText}
                        color="textSecondary"
                        testID="whiksyItemDistillationDate"
                    >
                        {distillationDate}
                    </Text>}
                    {bottlingDate && <Text
                        style={styles.headingText}
                        color="textSecondary"
                        testID="whiksyItemBottlingDate"
                    >
                        {bottlingDate}
                    </Text>}
                        {nameAddition && <Text
                        style={styles.headingText}
                        color="textSecondary"
                        testID="whiksyItemBottlingDate"
                    >
                        {nameAddition}
                    </Text>}
                    <Text
                        style={styles.headingText}
                        color="textSecondary"
                        testID="whiskyItemAbv"
                    >
                        {abv}%
                    </Text>
                    <Text
                        style={styles.headingText}
                        color="textSecondary"
                        testID="whiskyItemBottlesize"
                    >
                        {bottlesize}L
                    </Text>
                </View>
            </View>
            {/* <View style={styles.bottomContainer}>
        <CountItem
          count={reviewCount}
          label="Reviews"
          testID="repositoryItemReviews"
        />
        <CountItem
          count={ratingAverage}
          label="Rating"
          testID="repositoryItemRating"
        />
      </View> */}
        </View>
    );
};

export default WhiskyItemInfo;
