import React, { useState } from 'react';
import { View, StyleSheet, Text, TouchableWithoutFeedback} from 'react-native';
import { Button } from 'react-native-paper';
import { Formik } from 'formik';
import { useHistory } from 'react-router-native';
import * as yup from 'yup';
import FormikTextInput from './FormikTextInput';
import ChooseDate from './ChooseDate';
import theme from '../theme';

const styles = StyleSheet.create({
    container: {
      flexDirection: 'column',
      justifyContent: 'center',
      flexWrap: 'wrap',
      alignContent: 'center',
      height: '100%',
      width: '100%',
      position: 'absolute',
      flex: 1,
      zIndex: 1
    },
    fieldContainer: {
      marginBottom: 15,
    },
    buttonContainer: {
        position: 'absolute',
        zIndex: 2,
        paddingVertical: 10,
        paddingHorizontal: 15,
        borderWidth: 1,
        borderStyle: 'solid',
        // fontSize: theme.fontSizes.body,
        // fontFamily: theme.fonts.main,
        // color: theme.colors.textPrimary,
        borderRadius: 5,
        borderColor: '#aab2bb',
        marginBottom: 15,
    }, textContainer: {
        fontSize: theme.fontSizes.body,
        fontFamily: theme.fonts.main,
        color: theme.colors.textPrimary,
    }
  });

const initialValues = {
    firstName: '',
    lastName: '',
    country: '',
    bio: ''

};

const validationSchema = yup.object().shape({
    firstName: yup
        .string()
        .min(1, 'First name must be at least 1 charaters long')
        .required('First name is required'),
    lastName: yup
        .string()
        .min(1, 'Last name must be at least 1 characters long')
        .required('Last name is required'),
    birthdate: yup
        .date()
        .min(8, 'Birthdate must be atleast 8 characters')
        .max(8, 'Birthdate must be at most 8 characters long')
        .required('Birtdate is required'),
    country: yup
        .string()
        .required('Country is required'),
    bio: yup
        .string()
        .max(150, 'Bio can be at most 150 characters long'),
});

const submitDate = () => {
    return (<ChooseDate/>);
};


const UserinfoForm = ({ onSubmit }) => {


    return (
        <View style={styles.container}>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="firstName"
                    placeholder="First name" />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="lastName"
                    placeholder="Last name" />
            </View>

            {/* <View style={styles.fieldContainer}>
                <TouchableWithoutFeedback onPress={submitDate()}>
                    <FormikTextInput
                        value="date"
                        name="birthdate"
                        placeholder="Birthdate"
                    />
                </TouchableWithoutFeedback>
            </View> */}

            <View style={styles.buttonContainer}>
                <TouchableWithoutFeedback onPress={() => submitDate}>
                    <Text style={styles.textContainer}>Birthdate</Text>
                </TouchableWithoutFeedback>
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="country"
                    placeholder="Country"
                />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="bio"
                    placeholder="Bio"
                    multiline
                    numberOfLines={4}
                />
            </View>

        <Button mode={'outlined'} onPress={onSubmit}>
            Submit
        </Button>

        </View>
    );
};

const UploadUserInfo = () => {

    const history = useHistory();

    // const onSubmit = async (values) => {
    //     const { email, password } = values;

    //     SignUpWithEmail(email, password);

    //     history.push("/choose-pic");
    // };


    return (
        <>
            <Formik
                style={styles.container}
                initialValues={initialValues}
                onSubmit={() => { history.push('/'); }}
                validationSchema={validationSchema}
            >
                {({ handleSubmit }) => <UserinfoForm onSubmit={handleSubmit} />}
            </Formik>
        </>
    );
};

export default UploadUserInfo;
