import * as firebase from 'firebase';

export const sendChatMessage = (chatID, text, otherUserUid) => {

  firebase.firestore()
    .collection('messages')
    .doc(chatID)
    .collection('chats')
    .add({
      text: text,
      createdAt: new Date().getTime(),
      user: {
        _id: firebase.auth().currentUser.uid
      }
    });

    firebase.firestore()
    .collection('messages')
    .add({
      text: text,
      createdAt: new Date().getTime(),
      sendFrom: firebase.auth().currentUser.uid,
      sendTo: otherUserUid
    });    
};



const messagesRef = firebase.firestore().collection('messages');
const currentUser = firebase.auth().currentUser;

//We define an async function
export const getMyMessages = async () => {
  const sendFrom = messagesRef.where('sendFrom', '==', currentUser.uid).get();
  const sendTo = messagesRef.where('sendTo', '==', currentUser.uid).get();

  const [sendFromQuerySnapshot, sendToQuerySnapshot] = await Promise.all([
    sendFrom,
    sendTo
  ]);

  const sendFromArray = sendFromQuerySnapshot.docs;
  const sendToArray = sendToQuerySnapshot.docs;

  const myMessagesArray = sendFromArray.concat(sendToArray);

  console.log('my messages: ', myMessagesArray);

  return myMessagesArray;
};
