import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, ScrollView} from 'react-native';
import { Button, Avatar, Divider, IconButton  } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import firebase from "firebase/app";
import "firebase/firestore";
import "firebase/firebase-auth";


const styles = StyleSheet.create({

  container: {
    position: 'absolute',
    top: 100

  }

});


const MyInfo = () => {

  const history = useHistory();
  const [files, setFiles] = useState([]);

  useEffect(() => {
    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .doc("profilePicture")
    .get().then((snapshot) => {
      setImg(snapshot.data().downloadURL);
    });

  });


  return (
      <View style={styles.container}>
      <IconButton 
        icon={'check-circle-outline'} size={100} style={styles.iconButton} color='#2cf52c' />
      </View>
  );
};

export default MyInfo;
