import React from 'react';
import { StyleSheet, View, ScrollView } from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useHistory } from 'react-router-native';

import { Button} from 'react-native-paper';
import Text from './Text';
import FormikTextInput from './FormikTextInput';

import * as firebase from 'firebase';


const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  fieldContainer: {
    margin: 5,
  },
  heading: {
    alignItems: 'center',
    marginBottom: 30
  },
  buttoncontainer: {

  }
});

const initialValues = {
  name: '',
  distillery: '',
  country: '',
  type: '',
  age: '',
  abv: '',
  bottlesize: ''

};

const validationSchema = yup.object().shape({
  name: yup
    .string()
    .required('Name is required'),
  distillery: yup
    .string()
    .required('Distillery is required'),
  country: yup
    .string()
    .required('Country is required'),
  type: yup
    .string()
    .required('Type is required'),
  age: yup 
    .number()
    .required('Age is required'),
  abv: yup
    .number()
    .required('Abv is required'),
  bottlesize: yup
    .number()
    .required('Bottlesize is required')
});

const UploadWhiskyForm = ({ onSubmit }) => {
  return (
    <View>
      <View style={styles.fieldContainer}>
        <FormikTextInput 
        name="name" 
        placeholder="Name" />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="distillery"
          placeholder="Distillery"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="country"
          placeholder="Country"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="type"
          placeholder="Type"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="age"
          keyboardType="numeric"
          placeholder="Age"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="abv"
          keyboardType="numeric"
          placeholder="Abv"
        />
      </View>

      <View style={styles.fieldContainer}>
        <FormikTextInput
          name="bottlesize"
          keyboardType="numeric"
          placeholder="Bottlesize"
        />
      </View>

      <Button icon='camera' mode='outlined' style={styles.buttoncontainer}>Upload image</Button>

      <Button icon={'bottle-wine'} style={styles.fieldContainer} mode='outlined' onPress={onSubmit}>
          Upload whisky
      </Button>
    </View>
  );
};


const UploadWhisky = () => {
  const history = useHistory();

  const onSubmit = async (values) => {
    const {name, distillery, country, type, age ,abv, bottlesize} = values;

    firebase.firestore().collection('whiskies').add({
      name: name,
      distillery: distillery,
      country: country,
      type: type,
      age: age,
      abv: abv,
      bottlesize: bottlesize
    });
    history.push('/');
  };

  return (
    <View style={styles.container}>
      <ScrollView>
      <View style={styles.heading}>
        <Text color={'primary'} fontSize={'heading'} fontWeight={'bold'}>
          Upload Whisky
        </Text>
      </View>

      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        {({ handleSubmit }) => <UploadWhiskyForm onSubmit={handleSubmit} />}
      </Formik>

      </ScrollView>


    </View>
  );
};

export default UploadWhisky;
