import React, { useContext, useEffect, useRef, useState } from 'react';
import { Animated, StyleSheet, View, Dimensions, Image } from 'react-native';
import { Button, Divider, Drawer } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import Text from './Text';

import { StateContext } from '../state';
import CustomButton from './CustomButton';
import theme from '../theme';
import * as firebase from 'firebase';

const styles = StyleSheet.create({ 
  sidebar: {
    width: Dimensions.get('window').width*0.85,
    height: '100%', 
    backgroundColor: theme.colors.primary,
    zIndex: 4,
    position: 'absolute'
  },
  topContainer: {
    flexDirection: 'row',
    alignItems: 'center'
  },  
  nameText: {
    marginLeft: 10,
  },
  closeMenu: {
    backgroundColor: 'grey',
    width: '100%',
    height: '100%',
  },
  sidemenuButtons: {
    top: 50,
    zIndex:5,
    alignItems: 'flex-start'
  },
  button: {
    padding: 10
  },
  avatar: {
    width: 60,
    height: 60,
    marginLeft: 10,
    borderRadius: 50
  },
  divider: {
    zIndex: 5,
    marginTop: 10,
    width: '100%'
  }
 });

const SideMenu = () => {

  const toggleAnim = useRef(new Animated.Value(Dimensions.get('window').width*-0.85)).current; 
  const fadeAnim = useRef(new Animated.Value(0)).current ;
  const { state, dispatch } = useContext(StateContext);
  const history = useHistory();
  const [img, setImg] = useState('');
  const [username, setUsername] = useState('');

  const route = (route, number) => {

      setActive(number);
      history.push(route);
      dispatch({type: "SET_OPEN", payload: !state.open});
  };


  const moveMenu = () => {

    Animated.timing(
      toggleAnim,
      {
        toValue: state.open ? 0 : Dimensions.get('window').width*-0.85,
        useNativeDriver: true,
        duration: 300
      }
    ).start();

  };

  const fade = () => {
    Animated.timing(
      fadeAnim,
      {
        toValue: state.open ? 0.5: 0,
        duration: 300,
        useNativeDriver: true
      }
    ).start();
  };

  useEffect(() => {
    moveMenu();
    fade();
    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .doc("profilePicture")
    .get().then((snapshot) => {
      setImg(snapshot.data().downloadURL);
    });
    firebase.firestore()
    .collection("userinfo")
    .doc(firebase.auth().currentUser.uid)
    .get().then((snapshot) => {
      setUsername(snapshot.data().name);
    });

  },[state.open]);

  const [active, setActive] = useState('');

  console.log('currentUser: ', firebase.auth().currentUser);

  return (
    <>
      <Animated.View style={[styles.sidebar, {
        transform: [{
          translateX: toggleAnim
        }]
      }]}>
        <View style={styles.sidemenuButtons}>

        {!!img&&<View style={styles.topContainer}>
        <Image source={{ uri: img }} style={styles.avatar} />
        <Text style={styles.nameText} fontSize={'subheading'}>
          {username} logged in
        </Text>
        </View>}

        <View style={styles.topContainer}>
            <Button></Button>
            <Button></Button>
        </View>


          <Divider style={styles.divider}/>
          <Drawer.Section>
            <Drawer.Item
              icon="glass-tulip"
              label="Upload new whisky"
              active={active === 'first'}
              onPress={() => route('/upload-whisky', 'first')}
            />
          </Drawer.Section>
          <Drawer.Section>
            <Drawer.Item
              icon="account"
              label="Profile"
              active={active === 'second'}
              onPress={() => setActive('second')}
            />
          </Drawer.Section>
          <Drawer.Section>
            <Drawer.Item
              icon="forum"
              label="Forum"
              active={active === 'third'}
              onPress={() => setActive('third')}
            />
          </Drawer.Section>
          <Drawer.Section>
            <Drawer.Item
              icon="forum"
              label="Forum"
              active={active === 'third'}
              onPress={() => setActive('third')}
            />
          </Drawer.Section>



          <Button color='white' icon='account' style={styles.button} onPress={() => {route('/upload-whisky');}}>
            Profile
          </Button>

          <Button color='white' icon='account' style={styles.button} onPress={() => {route('/sign-up');}}>
            Profile
          </Button>
          <Button color='white' icon='star-box' style={styles.button} onPress={() => {console.log('Reviews pressed');}}>
            Reviews
          </Button>
          <Button color='white' icon='map' style={styles.button} onPress={() => {console.log('Map pressed');}}>
            Map
          </Button>
          <Button color='white' icon='shopping' style={styles.button} onPress={() => {console.log('Market pressed');}}>
            Market
          </Button>
        </View>
      </Animated.View>
      <Animated.View
        style={[styles.closeMenu, {opacity: fadeAnim, zIndex: state.open ? 3 : 0} ]}
      >
          {state.open &&
          <CustomButton style={{height: "100%", borderRadius: 0}} color='' onPress={() => dispatch({type: "SET_OPEN", payload: !state.open})} ></CustomButton> }

      </Animated.View>
    </>
  );
};

export default SideMenu;
