import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Image, ActivityIndicator, Dimensions} from 'react-native';
import { Button, Avatar, Divider } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import { TabView, SceneMap } from 'react-native-tab-view';

import { StateContext } from '../state';
import Text from './Text';

import {
  Title,
  Paragraph,
} from 'react-native-paper';
import {
  Tabs,
  TabScreen,
  useTabIndex,
  useTabNavigation,
} from 'react-native-paper-tabs';


import firebase from "firebase/app";
import "firebase/auth";
import "firebase/firestore";

import theme from '../theme';

const styles = StyleSheet.create({
  topContainer: {
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1
  },
  container: {
    // flexDirection: 'row',
    alignItems: 'center',
  }, 
  bottomContainer: {

  },
  avatar: {
    marginBottom: 50,
    width: 150, 
    height: 150,
    borderRadius: 100
  },
  loading: {
    position: 'absolute',
    top: 245,
    zIndex: 2
  },
  button: {
    marginHorizontal: 50
  },
  button2: {
    marginTop: 20
  },
});

const FirstRoute = () => (
  <View style={{ flex: 1, backgroundColor: '#ff4081' }} />
);

const SecondRoute = () => (
  <View style={{ flex: 1, backgroundColor: '#673ab7' }} />
);

const renderScene = SceneMap({
  first: FirstRoute,
  second: SecondRoute,
});

function Example() {
  return (
    <Tabs
      // defaultIndex={0} // default = 0
      // uppercase={false} // true/false | default=true | labels are uppercase
      // showTextLabel={false} // true/false | default=false (KEEP PROVIDING LABEL WE USE IT AS KEY INTERNALLY + SCREEN READERS)
      // iconPosition // leading, top | default=leading
      // style={{ backgroundColor:'#fff' }} // works the same as AppBar in react-native-paper
      // dark={false} // works the same as AppBar in react-native-paper
      // theme={} // works the same as AppBar in react-native-paper
      // mode="scrollable" // fixed, scrollable | default=fixed
      // onChangeIndex={(newIndex) => {}} // react on index change
      // showLeadingSpace={true} //  (default=true) show leading space in scrollable tabs inside the header
    >
      <TabScreen label="Explore" icon="compass">
         <ExploreWitHookExamples />
      </TabScreen>
      <TabScreen label="Flights" icon="airplane">
        <View style={{ backgroundColor: 'black', flex:1 }} />
      </TabScreen>
      <TabScreen label="Trips" icon="bag-suitcase">
         <View style={{ backgroundColor: 'red', flex:1 }} />
      </TabScreen>
    </Tabs>
  )
}

function ExploreWitHookExamples() {
const goTo = useTabNavigation();
const index = useTabIndex();
return (
  <View style={{ flex:1 }}>
    <Title>Explore</Title>
    <Paragraph>Index: {index}</Paragraph>
    <Button onPress={() => goTo(1)}>Go to Flights</Button>
  </View>
);
}



const Userpage = () => {
  const history = useHistory();
  const [img, setImg] = useState();
  const [loaded, setLoaded] = useState(false);
  const [index, setIndex] = useState(0);
  const [routes] = useState([
    { key: 'first', title: 'First' },
    { key: 'second', title: 'Second' },
  ]);

  useEffect(() => {
    firebase.firestore()
    .collection("images")
    .doc(firebase.auth().currentUser.uid)
    .collection("userImages")
    .doc("profilePicture")
    .get().then((snapshot) => {
      setImg(snapshot.data().downloadURL);
    });

  },[history]);

  const linkTo = (link) => {
    history.push(link);
  };

  return (

      <View style={styles.topContainer}>
      <View style={styles.loading}>
        <ActivityIndicator size='large' animating={!loaded} color={theme.colors.primary} />
      </View>
      <Image source={{ uri: img }} style={styles.avatar} onLoadEnd={() => setLoaded(true)} defaultSource={''} />
      {/* <View style={styles.container}>
        <Button icon="star" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-reviews')}>
          My reviews
          </Button>

        <Button icon="information" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-info')}>
          My info
          </Button>

        <Button icon="database" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-collection')}>
          My collection
          </Button>

        <Button icon="glass-tulip" mode="outlined" compact="true" style={styles.button} onPress={() => linkTo('my-wishList')}>
          My wish List
          </Button>

      </View> */}

      </View>

  );
};

export default Userpage;
