import React, { useContext, useEffect, useRef } from 'react';
import { Animated, StyleSheet, View, Text, Dimensions } from 'react-native';
import { Button } from 'react-native-paper';
// import useResizeAware from 'react-resize-aware';

import { StateContext } from '../state';
import CustomButton from './CustomButton';
import theme from '../theme';

const styles = StyleSheet.create({ 
  sidebar: {
    width: Dimensions.get('window').width*0.9,
    height: '100%', 
    backgroundColor: theme.colors.primary,
    zIndex: 4,
    position: 'absolute'
  },
  closeMenu: {
    backgroundColor: 'grey',
    width: '100%',
    height: '100%',
  },
  sidemenuButtons: {
    zIndex:5
  }
});

const SideMenu = () => {

  // const [resizeListener, sizes] = useResizeAware();

  const toggleAnim = useRef(new Animated.Value(Dimensions.get('window').width*-0.8)).current; 
  const fadeAnim = useRef(new Animated.Value(0)).current ;
  const { state, dispatch } = useContext(StateContext);


  const moveMenu = () => {

    Animated.timing(
      toggleAnim,
      {
        toValue: state.open ? 0 : Dimensions.get('window').width*-0.8,
        useNativeDriver: true,
        duration: 300
      }
    ).start();

  };

  const fade = () => {
    Animated.timing(
      fadeAnim,
      {
        toValue: state.open ? 0.15: 0,
        duration: 300,
        useNativeDriver: true
      }
    ).start();
  };

  useEffect(() => {
    moveMenu();
    fade();
  },[state.open]);

  return (
    <>
      {/* {resizeListener} */}
      <Animated.View style={[styles.sidebar, {
        transform: [{
          translateX: toggleAnim
        }]
      }]}>
        <View style={styles.sidemenuButtons}>
          <Button>Profile</Button>
          <Button>Reviews</Button>
          <Button>Community</Button>
          <Button>Marketplace</Button>
        </View>
      </Animated.View>
      <Animated.View
        style={[styles.closeMenu, {opacity: fadeAnim, zIndex: state.open ? 3 : 0} ]}
      >
          {state.open &&
          <CustomButton style={{height: "100%", borderRadius: 0}} color='' onPress={() => dispatch({type: "SET_OPEN", payload: !state.open})} ></CustomButton> }

      </Animated.View>
    </>
  );
};

export default SideMenu;
