import React, { useState, useContext, useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, ScrollView, Image, FlatList} from 'react-native';
import { Button, Avatar, Divider, IconButton  } from 'react-native-paper';
import { useHistory } from 'react-router-native';
import * as firebase from 'firebase';
import Text from './Text';


const styles = StyleSheet.create({

  container: {
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100%',
    width: '100%',
    position: 'absolute',
    zIndex: 1

  },
  normalText: {

  },
  bioText: {

  },
  containerGaller: {

  }, 
  containerImage: {
    padding: 10

  },
  image: {
    width: 50,
    height: 50
  },
  separator: {
    height: 5,
  },
  avatarContainer: {
    height: 100,
    width: 100,
    backgroundColor: 'red'
  }


});




const MyInfo = () => {


  const [userinfo, setUserinfo] = useState([]);

  useEffect(() => {
    firebase.firestore().collection('userinfo').doc(firebase.auth().currentUser.uid)
    .get().then((doc) => {setUserinfo(doc.data());});

  },[]);



  return (
    <View style={styles.container}>

      <View>
        <Text
          style={styles.normalText}
          fontSize="subheading"
        >
          Name: {userinfo.name}
        </Text>

        <Text
          style={styles.normalText}
          fontSize="subheading"
        >
          Birthdate: {userinfo.birthdate}
        </Text>


        <Text
          style={styles.bioText}
          fontSize="subheading"
        >
          Bio: {userinfo.bio}
        </Text>
      </View>

    </View>
  );
};

export default MyInfo;
