import React, { useContext, useEffect, useRef } from 'react';
import { Animated, StyleSheet, View, Text, Dimensions } from 'react-native';
import useResizeAware from 'react-resize-aware';

import { StateContext } from '../state';
import Button from './Button';
import theme from '../theme';

const styles = StyleSheet.create({ 
  sidebar: {
    width: Dimensions.get('screen').width*0.8,
    height: '100%', 
    backgroundColor: theme.colors.primary,
    zIndex: 4,
    position: 'absolute'
  },
  closeMenu: {
    backgroundColor: 'grey',
    width: '100%',
    height: '100%',
  }
});

const SideMenu = () => {

  const [resizeListener, sizes] = useResizeAware();

  const toggleAnim = useRef(new Animated.Value(Dimensions.get('screen').width*-0.8)).current; 
  const fadeAnim = useRef(new Animated.Value(0)).current ;
  const { state, dispatch } = useContext(StateContext);



  const moveMenu = () => {

    Animated.timing(
      toggleAnim,
      {
        toValue: state.open ? 0 : Dimensions.get('screen').width*-0.8,
        // toValue: state.open ? 0 : -600,
        duration: 300
      }
    ).start();

  };

  const fade = () => {
    Animated.timing(
      fadeAnim,
      {
        toValue: state.open ? 0.15: 0,
        duration: 300,
      }
    ).start();
  };

  useEffect(() => {
    moveMenu();
    fade();
  },[state.open]);

  return (
    <>
      {resizeListener}
      <Animated.View style={[styles.sidebar, {
        transform: [{
          translateX: toggleAnim
        }]
      }]}>
        <View style={styles.button}>
          <Text>User name</Text>
          <Text> Create new program </Text>
          <Text> Search for programs </Text>
        </View>
      </Animated.View>
      <Animated.View
        style={[styles.closeMenu, {opacity: fadeAnim, zIndex: state.open ? 3 : 0} ]}
      >
          {state.open &&
          <Button style={{height: "100%", borderRadius: 0}} color='' onPress={() => dispatch({type: "SET_OPEN", payload: !state.open})} ></Button> }

      </Animated.View>
    </>
  );
};

export default SideMenu;
