import React from 'react';
import { View, Image, StyleSheet } from 'react-native';
import Text from './Text';

const styles = StyleSheet.create({
  backgroundCountainer: {
    backgroundColor: 'orange',
  },
  container: {
    padding: 5,
    width: '100%',
    flexDirection: 'row',
    backgroundColor: '#FFD700',
  },
  contentContainer: {
    borderRadius: 5,
    width: '60%',
    flexGrow: 1,

    backgroundColor: '#f2eecb',
  },
  middleContainer: {
    alignItems: 'center',
    padding: 5,
    flexGrow: 0,
    // backgroundColor: 'brown'

  },
  bottomContainer: {
    flexGrow: 0,
    padding: 5,
    // backgroundColor: 'blue',
    justifyContent: 'space-between',
    flexDirection: 'row',
  },
  avatarContainer: {
    marginRight: 5,
  },
  topContainer: {
    flexGrow: 1,
    padding: 5,
    // backgroundColor: 'purple'
    // alignItems: 'center'
  },
  nameText: {
    marginBottom: 5,
  },
  avatar: {
    width: 120,
    height: 180,
    borderRadius: 5,
  },
  countItem: {
    flexGrow: 0,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 15,
  },
  countItemCount: {
    marginBottom: 5,
  },
});

const CountItem = ({ label, count, ...props }) => (
  <View style={styles.countItem}>
    <Text style={styles.countItemCount} fontWeight="bold" {...props}>
      {count}
    </Text>
    <Text color="textPrimary">{label}</Text>
  </View>
);

const WhiskyItem = ({ whisky }) => {
  const {
    brand,
    nameAddition,
    age,
    distillationDate,
    bottlingDate,
    abv,
    bottleSize,
    downloadURL,
    reviewCount,
    rating,
    bottler,
  } = whisky;f

  return (
    <View style={styles.backgroundCountainer}>

      <View style={styles.container}>

        <View style={styles.avatarContainer}>
          <Image source={{ uri: downloadURL }} style={styles.avatar} />
        </View>
        <View style={styles.contentContainer}>
          <View style={styles.topContainer}>
            <Text
              style={styles.nameText}
              testID="whiskyItemName"
              fontSize="subheading"
              fontWeight="bold"
              numberOfLines={3}
            >
              {brand && `${brand} `}
              {age && `${age} `}
              {distillationDate
            && `${distillationDate}/`}
              {bottlingDate && `${bottlingDate} `}
              {nameAddition && `${nameAddition} `}
              {abv && `${abv}% `}
              {bottleSize && `${bottleSize}L`}
            </Text>
          </View>
          <View style={styles.middleContainer}>
            <Text
              style={styles.nameText}
              fontSize="subheading"
              testID="whiskyItemBottlesize"
            >
              {bottler}
            </Text>
          </View>
          <View style={styles.bottomContainer}>
            <CountItem
              count={rating}
              label="Rating"
              testID="repositoryItemRating"
            />
            <CountItem
              count={reviewCount}
              label="Reviews"
              testID="repositoryItemReviews"
            />
          </View>
        </View>

      </View>
    </View>
  );
};

export default WhiskyItem;
