import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import { Button } from 'react-native-paper';
import { Formik } from 'formik';
import { useHistory } from 'react-router-native';
import * as yup from 'yup';
import FormikTextInput from './FormikTextInput';

const styles = StyleSheet.create({
    container: {
      flexDirection: 'column',
      justifyContent: 'center',
      flexWrap: 'wrap',
      height: '100%',
      width: '100%',
      position: 'absolute',
      zIndex: 1
    },
    // fieldContainer: {
    //   marginBottom: 15,
    // },
  });

const initialValues = {

};

const validationSchema = yup.object().shape({
    firstName: yup
        .string()
        .min(1, 'First name must be at least 1 charaters long')
        .required('First name is required'),
    lastName: yup
        .string()
        .min(1, 'Last name must be at least 1 characters long'),
    birthdate: yup
        .date()
        .min(8, 'Birthdate must be atleast 8 characters')
        .max(8, 'Birthdate must be at most 8 characters long')
        .required('Birtdate is required'),
    country: yup
        .string()
        .required('Country is required'),
    bio: yup
        .string()
        .max(150, 'Bio can be at most 150 characters long'),
});

const UserinfoForm = ({ onSubmit }) => {
    return (
        <View style={styles.container}>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="firstName"
                    placeholder="First name" />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="lastName"
                    placeholder="Last name" />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="birthdate"
                    placeholder="Birthdate"
                />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="country"
                    placeholder="Country"
                />
            </View>

            <View style={styles.fieldContainer}>
                <FormikTextInput
                    name="bio"
                    placeholder="Bio"
                    multiline
                />
            </View>

        <Button onPress={onSubmit}>
            Submit
        </Button>

        </View>
    );
};

const UploadUserInfo = () => {

    const history = useHistory();


    return (

            <Formik
                style={styles.container}
                initialValues={initialValues}
                onSubmit={()=>{history.push('/');}}
                validationSchema={validationSchema}
            >
                {({ handleSubmit }) => <UserinfoForm onSubmit={handleSubmit} />}
            </Formik>
    );
};

export default UploadUserInfo;
