import * as firebase from 'firebase';

export const sendChatMessage = (chatID, text) => {

  firebase.firestore()
    .collection('messages')
    .doc(chatID)
    .collection('chats')
    .add({
      text: text,
      createdAt: new Date().getTime(),
      user: {
        _id: firebase.auth().currentUser.uid
      }
    });
};
