import * as firebase from 'firebase';

export const sendChatMessage = (chatID, chat) => {
  console.log('chatid: ', chatID);
  console.log('chat: ', chat);
  firebase.firestore()
    .collection('messages')
    .doc(chatID)
    .collection('chats')
    .add({
      chat: chat,
      createdAt: new Date().getTime(),
      user: {
        _id: firebase.auth().currentUser.uid,
      }
    });
};
